INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3135, 'Bistro Manu', 'Bistro Manu on Kampissa sijaitseva kortteliravintola, jossa mutkaton ruoka- ja juoma yhdistyvät mestarilliseen palveluun. Ravintola sijaitsee Original Sokos Hotel Presidentissä ja on avoinna joka päivä lounaasta iltaan. Menneinä aikoina manu oli nimitys mestarille ja oman alansa parhaalle ammattilaiselle. Bistro Manussa ruoka ja juoma tarjoillaan tämän päivän mestarillisella otteella. Ravintolan mestarien kädentaidot löytyvät soppakauhan varresta, cocktail-lasista sekä vieraita hurmaavista ravintolaelämyksistä. Bistro Manu kutsuu kylään nykypäivän mestarit ja arjen sankarit hyvän palvelun, ruoan ja juoman äärelle.', 'https://www.raflaamo.fi/fi/helsinki/bistro-manu', NULL, 'Eteläinen Rautatiekatu 4', '00100', 'Helsinki', '2022-01-30 17:05:36.328099+02', 10);
