INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3160, 'GLO Grill Kitchen', 'Espoossa sijaitsevan GLO Hotel Sellon yhteydessä oleva ravintola GLO Grill Kitchen on tyylikäs ja muuntautumiskykyinen tila sekä työskentelyyn että ruokailuun. GLO Grill Kitchenin aidossa hiiligrillissä valmistuvat sesonginmukaiset herkut - niin aamiaiselle, lounaalle kuin illallisellekin.', 'https://www.glohotels.fi/hotellit/glo-sello/ravintola', NULL, 'Leppävaarankatu 1, GLO Hotel Sello', '02600', 'Espoo', '2022-01-30 17:05:36.508938+02', 12);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (4297, 'Grape Wine Bar', 'Kotoisa kolmen naisen viinibaari herkullisilla viineillä ja vaihtuvalla baarimenulla.', 'https://www.grapewinebar.fi/', 'https://tprimages.blob.core.windows.net/public/4297/fc1543f8-3958-4f56-aaf1-ca5f322f6a8e.jpg', 'Uudenmaankatu 13', '00120', 'Helsinki', '2022-01-30 17:05:36.514692+02', 10);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3493, 'Gran Delicato Wine Bar & Taverna', 'Kreikkalainen taverna, ouzerí ja viinibaari tarjoilee kreikkalaisia viinejä ja ouzoja sekä näiden kanssa sopivia pienempiä mezédesiä ja suurempia ruoka-annoksia. Oma kreikkalainen juhlailta jopa 30 hengelle.', 'https://www.facebook.com/grandelicato/', 'https://tprimages.blob.core.windows.net/public/3493/16e77f31-8fbb-4310-8a94-79b36ead641b.jpg', 'Albertinkatu 31', '00180', 'Helsinki', '2022-01-30 17:05:36.517715+02', 10);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (4320, 'Groovy', 'Käpylässä sijaitsevan Groovy-ravintolan ruokalistalta löytyy niin napolilaisia pizzoja kuin laajasti myös erilaisia burgereita lisukkeineen.', 'https://www.groovyfood.fi/', NULL, 'Mäkelänkatu 87', '00610', 'Helsinki', '2022-01-30 17:05:36.523133+02', 19);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2602, 'Grön', 'Grön on maukasta, modernia, kasvipainotteista sekä inspiroivaa ruokaa, jossa panostetaan korkealaatuisiin raaka-aineisiin. Ruoka on myös kausi- ja luomukeskeistä. Ravintolassa käytetään mahdollisimman paljon pohjoismaalaisia ja luonnon omia villituotteita. Mitä luonnossa tällä hetkellä kasvaa, määrittää sen mitä on tarjolla. 
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (4220, 'Grill it! Tapiola Garden & Garden Bar', 'Espoossa sijaitsevan Tapiola Gardenin viihtyisä Grill it! -ravintola yhdistää perinteisen grillauksen ja ensiluokkaiset raaka-aineet tuoreella tavalla. Ravintolan sielu löytyy keittiöstä: puuhiiligrilli, joka tuo lihan, kalan ja vihannesten aromit hyvin esiin. 
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2137, 'Haltia Suomen luontokeskus', 'Suomen luonto- ja retkeilykeskus Haltia on elämyksellinen vierailukohde, jonka näyttelyissä saat aidon elämyksen koko Suomen luonnosta. Haltian luontonäyttelyissä pääset Suomen kansallispuistojen mahtaviin maisemiin ja luontopääkaupunki Helsingin luontokohteisiin.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3280, 'Hanasaari - Hanaholmen', 'Aivan Helsingin ja Espoon rajalla, Länsiväylän varrella sijaitsee Hanasaari - suomalais-ruotsalainen kulttuurikeskus, jossa on nykyaikaiset kongressitilat, laadukas ravintola ja hotelli, jonka kaikista huoneista avautuu merinäköala. Hanasaaressa saaristoluonto kohtaa nykytaiteen: lähiympäristössä on sekä pysyviä että tilapäisiä teoksia pohjoismaisilta taiteilijoilta, kuten Jeppe Heinilta, Kaarina Kaikkoselta ja Anna Uddenbergiltä. Hanasaaren ravintola Platsissa tarjoillaan kauden parhaista raaka-aineista valmistettua ruokaa, jossa puhtaat, pohjoismaiset maut, kestävä maatalous ja eettinen ajattelu kohtaavat. Hanasaaressa toimii myös galleria, jonka näyttelyohjelma valottaa taiteen ja designin ajankohtaisia ilmiöitä. Tilavat hotellihuoneet on sisustettu pohjoismaisittain, ja ikkunoista avautuva saaristolaismaisema hivelee silmiä.', 'https://www.hanaholmen.fi/', 'https://tprimages.blob.core.windows.net/public/3280/6c3f2d21-a135-4568-92c8-30e892b10dc0.jpg', 'Hanasaarenranta 5', '02100', 'Espoo', '2022-01-30 17:05:36.53508+02', 4);
