INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (4285, 'Harbour Tap & Taste', 'Kalasataman Capellanaukiolla sijaitseva Harbour Tap & Taste on alueen olohuone sekä ruokaravintola, joka panostaa myös laajaan olutvalikoimaan. Ravintola sijaitsee rannassa ja sillä on merinäköala Mustikkamaan sekä Isoisänsillan suuntaan. Myös terassi.', 'https://www.ravintolaharbour.fi/', NULL, 'Capellanaukio 1', '00540', 'Helsinki', '2022-01-30 17:05:36.570229+02', 25);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3632, 'Harju 8', 'Harju 8 on fiilisravintola Kalliossa jossa tarjolla on hyvää ruokaa ja natural-viinejä. Lisäksi tiloissa toimii Kukkakollektiivin kukkakauppa.', 'http://harju8.fi/', 'https://tprimages.blob.core.windows.net/public/3632/252dad94-e96a-447e-8757-4618031ae381.jpg', 'Harjutori 8', '00500', 'Helsinki', '2022-01-30 17:05:36.573424+02', 36);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3725, 'Hello Stranger', 'Hello Stranger on ravintola ja cocktailbaari keskustassa jonka menussa yhdistyvät lähiruoka, sesongit ja keittiömestari Tom Hudsonin englantilainen tausta; ravintola tarjoilee satokauden parhaita raaka-aineita erilaisia tekniikoita, makuja ja vaikutteita ennakkoluulottomasti hyödyntäen.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3421, 'Hills Dumplings', 'Hills Dumplings on erikoistunut käsintehtyihin mykyihin eli dumplingseihin. Lisäksi tarjolla modernin aasialaisen keittiön herkkuja.', 'https://www.hillsdumplings.com', NULL, 'Pieni Roobertinkatu 9', '00130', 'Helsinki', '2022-01-30 17:05:36.579341+02', 49);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (482, 'Hima & Sali', 'Hima & Sali on kahvila ja lounasravintola Kaapelitehtaalla. Lounas tarjoillaan arkisin klo 11-14.30 ja päivittäin vaihtuvalta listalta löytyy vaihtoehtoja jokaiselle. Iltapäivisin tarjolla on pizza ja keittomenu.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3164, 'Hoku Kamppi', 'Hoku on mutkaton ravintola Kampin keskuksessa joka tarjoilee aasialais-hawaijilaista ruokaa.', 'https://www.hoku.fi/', NULL, 'Urho Kekkosen katu 1, Kampin kauppakeskus 5.krs.', '00100', 'Helsinki', '2022-01-30 17:05:36.584627+02', 10);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3195, 'Home Taste', 'Home Taste on aasialaista fuusioruokaa tuoreista raaka-aineista tarjoava ravintola Kauppakeskus Kluuvissa. Lounasbuffetissa on tarjolla lämpimiä ruokia sekä sushia.', 'http://hometaste.fi/', NULL, 'Aleksanterinkatu 9, Kauppakeskus Kluuvi K1', '00100', 'Helsinki', '2022-01-30 17:05:36.587462+02', 30);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2623, 'HSS Paviljong', 'Merisataman rannan edustalla Liuskasaaressa sijaitsevalla ravintola HSS Paviljongilla on kaupungin upeimpiin lukeutuva näköala. Samalta terassilta voi ihailla niin kantakaupungin vanhoja jugendtaloja kuin Suomenlahdelle aukeavaa ulappaa ohilipuvine purjeveneineen.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (87, 'Ichiban Sushi', 'Ichiban Sushi on japanilainen perhe-ravintola jossa on kotoinen ja ystävällinen ilmapiiri.', 'https://www.ichiban.fi/', NULL, 'Urho Kekkosen katu 1, Kamppi Helsinki E-taso', '00100', 'Helsinki', '2022-01-30 17:05:36.593655+02', 10);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2099, 'Iguana Mannerheimintie', 'Mannerheimintien Iguana on rento ruoka- ja seurusteluravintola kahdessa kerroksessa. Keittiöstä saa nopeasti tex-mex-herkkuja, pizzaa ja hampurilaisia. Keittiö on avoinna sulkemiseen asti. Buffetlounas on tarjolla ma-pe 11-14.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3575, 'IL Gabbiano', 'Kauppakeskus Sellossa sijaitseva IL Gabbiano tarjoilee suurella tunteella höystettyjä herkullisia italialaisia pizzoja, pastoja sekä jäätelöitä. Lämminhenkinen tunnelma houkuttelee niin lounaalle kuin illanviettoon hyvästä ruoasta nauttien!', 'https://www.ilgabbiano.fi/', NULL, 'Ratsukatu 3, Kauppakeskus Sello', '02600', 'Espoo', '2022-01-30 17:05:36.600517+02', 12);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3413, 'Inari', 'Ravintola Inari tarjoilee uuden aallon helsinkiläistä ruokaa. Keittiömestari Kim Mikkola on suunnitellut ainutlaatuisen 15 ruokalajin maistelumenun, jotka yhdistelevät innovatiivisella tavalla suomalaista kulttuuria ja aasialaisia vaikutteita.
