INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2742, 'Just Vege', 'Just Vege kasvispikaruokapaikka Kalliossa Lähi-idän vaikutteilla. Monen mielestä täältä saa kaupungin parhaat falafelit, ja niistä ravintolassa ollaankin erityisen ylpeitä. Falafelit ovat gluteenittomia ja ne tehdään alusta asti itse.', 'https://www.justvege.fi/', NULL, 'Vaasankatu 15', '00500', 'Helsinki', '2022-01-30 17:05:36.639738+02', 2);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (1602, 'Kaarna Baari & Keittiö', 'Kaarna baari & keittiö on rento suomalainen ravintola pohjoismaalaisella mausteella. Forumin kauppakeskuksessa sijaitsevan ravintolan keittiö tarjoaa yksinkertaisia, selkeitä ja puhtaita makuja. Reilujen annosten lisäksi ruokalistalta löytyy myös kotimaisista raaka-aineista valmistettuja snapaksia ja snapaslautoja. Monipuolisessa juomatarjonnassa korostuvat suomalaisten ja pohjoismaalaisten pienpanimoiden tuotteet. Keskeisen sijaintinsa ansiosta Kaarna baari & keittiö on erinomainen treffauspaikka.', 'https://www.raflaamo.fi/fi/helsinki/kaarna-baari-keittio', NULL, 'Mannerheimintie 20', '00100', 'Helsinki', '2022-01-30 17:05:36.642199+02', 10);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (85, 'Kabuki', 'Ravintola Kabuki on yksi Helsingin vanhimmista perinteisistä japanilaisista ravintoloista. Tatamilla on tunnelmallista nauttia perinteisestä japanilaisesta ruuasta ja suhshista aitoon japanilaiseen tapaan. Rajallisen tilan tähden pöytävarauksen tekeminen onkin suositeltavaa.', 'https://kabuki.fi/', NULL, 'Lapinlahdenkatu 12', '00180', 'Helsinki', '2022-01-30 17:05:36.645374+02', 10);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2651, 'Kampai3', 'PIeni ravintola Kalliossa jossa itäaasialainen fuusiokeittiö.', 'https://www.kampai3.fi/', NULL, 'Kolmas linja 18', '00530', 'Helsinki', '2022-01-30 17:05:36.648587+02', 36);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (16, 'Karjalatalo', 'Käpylässä sijaitseva Karjalatalo on monien toimintojen talo, jossa on tiloja kulttuurin harrastajille, ruokailijoille, juhlijoille ja uuden opiskelijoille. Talossa on myös karjalaista tunnelmaa.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (65, 'Katajanokan Kasino', 'Katajanokan Kasino tarjoaa tyylikkään ja historiallisen ravintolamiljöön. Mottona on tarjota asiakkaille ensiluokkaisia palveluja perinteitä kunnioittaen, nykypäivää eläen, ja tulevaisuuteen uskoen. Katajanokan Kasino on pääsääntöisesti tilausravintola, mutta palvelee ajoittain lounasravintolana sekä toisinaan myös a la carte -ravintolana, mm. joulun aikaan.', 'https://www.katajanokankasino.fi/', NULL, 'Laivastokatu 1', '00160', 'Helsinki', '2022-01-30 17:05:36.654981+02', 52);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2899, 'Kellohalli', 'Teurastamon alueella sijaitseva ravintola ja tapahtumatila Kellohalli tarjoilee lounasta arkisin. Kesäaikaan Kellohalli on avoinna myös iltaisin. Avoinna myös Teurastamon alueen tapahtumien aikaan sekä tilauksesta.', 'https://www.kellohalli.fi/', NULL, 'Työpajankatu 2, rakennus 1 e', '00580', 'Helsinki', '2022-01-30 17:05:36.657878+02', 43);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2287, 'Kiasma Café', 'Nykytaiteen museo Kiasmassa sijaitseva Kiasma Café tarjoaa makuelämyksiä sesonkeja kunnioittaen. Kiasma Café tarjoilee päivittäin vaihtuvaa lounasta lautasannoksina tiistaista perjantaihin klo 11-14. Lämpimän lounaan lisäksi tarjolla on salaattiannos, jonka komponentit asiakas saa itse valita tarjolla olevista vaihtoehdoista. Salaattiannos on tarjolla tiistaista sunnuntaihin klo 11-17. Lisäksi vitriinit notkuvat herkullisia itse tehtyjä tuoreita kahvilatuotteita. Kiasman kahvilassa sopii myös nauttia lasillisen viiniä museovierailun yhteydessä!', 'https://www.cafekiasma.fi/', NULL, 'Mannerheiminaukio 2', '00100', 'Helsinki', '2022-01-30 17:05:36.660651+02', 30);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2404, 'KIILA Food & Bar', 'Ravintola Kiila on auki joka päivä aamun sarastuksesta iltahämärään. Kiila tarjoaa asiakkailleen raikkaita, rehellisiä ja rennon tyylikkäitä makuelämyksiä. Ravintola toimii oivana kohtauspaikkana tarjoten upeat näkymät Kolmen Sepän aukiolle. Kiilassa on kaksi ruokailupuolta - KiilaBurger ja Kiila A''la Carte.', 'https://www.ravintolakiila.fi/', NULL, 'Kalevankatu 1', '00100', 'Helsinki', '2022-01-30 17:05:36.663371+02', 10);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3617, 'King Kebab Vallila', 'King Kebab on vankan asiakaskuntansa ansainnut perheyritys joka on toiminut vuodesta 2007. King Kebabin pikaruokaravintolat Vallilassa, Kalliossa ja Herttoniemessä tarjoilevat mm. perinteistä kebabia ja pizzaa sekä vegaanisia pizzoja ja vöneriä.', 'https://kingkebab.fi', NULL, 'Mäkelänkatu 31', '00550', 'Helsinki', '2022-01-30 17:05:36.665823+02', 32);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3610, 'Kipsari', 'Espoon Otaniemessä sijaitseva Kipsari on Aalto ARTSin opiskelijoiden omistama ravintola, baari ja kahvila, joka tarjoilee kasvis- ja vegaaniruokaa sekä paikan päällä leivottuja herkkuja.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3073, 'Kitchen & Table Helsinki', 'Huippukokki Marcus Samuelssonin suunnittelemissa Kitchen & Table -ravintoloissa ammennetaan New Yorkin monimuotoisesta ruokakulttuurista, mutta pohjoismaisella tyylillä. Jätkäsaaren ikonisesta 30-luvun makasiinirakennuksesta löytyvän ravintolan menussa Harlem kohtaa Herttoniemen sulassa sovussa. Ravintola on pieni pala Manhattania Jätkäsaaren rannassa, kivenheiton päässä keskustasta ‒ mutkaton, herkullinen ja elegantti.', 'https://kitchenandtable.se/fi/helsinki/', NULL, 'Tyynenmerenkatu 2, Clarion Hotel Helsinki', '00220', 'Helsinki', '2022-01-30 17:05:36.671213+02', 18);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (4235, 'Kitchen & Table Helsinki Airport', 'Clarion Hotel Aviapolis -hotellin Kitchen & Table -ravintola ja baari ovat ruotsalaisen huippukokki Marcus Samuelssonin käsialaa. Urbaanin tyylikkään ravintolan menussa yhdistyvät Manhattanin maut ja suomalaiset raaka-aineet. Kesäisin voi nauttia auringosta ravintolan ulkoterassilla.', 'https://kitchenandtable.se/fi/helsinki-airport/', NULL, 'Karhumäentie 5', '01530', 'Vantaa', '2022-01-30 17:05:36.673518+02', 45);
