INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3071, 'Krapihovi', 'Ravintola Krapihovi Tuusulassa vaalii ja luo suomalaista ruokaperinnettä idyllisessä huvilaympäristössä. Helmenharmaa, vihreäpuitteinen päärakennus on rakennettu vuonna 1883 valtioneuvos Souranderin kesähuvilaksi ja Krapin tilan päärakennukseksi. 
