INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (4006, 'Kulma - keittö & baari', 'Kulma - Keittiö & Baari on koko perheen kortteliravintola Torpparinmäessä. Kulman Keittiö käyttää mahdollisimman paljon kotimaisia raaka-aineita ja lähituottajia ja Kulman Baarissa pääasiassa on kotimaiset panimotuotteet. lisäksi ravintolassa järjestetään tapahtumia.', 'https://kulmakeittio.fi/', NULL, 'Lainlukijantie 12', '00690', 'Helsinki', '2022-01-30 17:05:36.699386+02', 42);
