INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3866, 'Mekong ravintola', 'Mekong ravintolan missio on tarjoilla vietnamilaisten kotikokkien reseptien mukaan valmistettua ruokaa asiakkailleen. Ravintola tarjoilee autenttista lounais-vietnamilaista ruokaa, myös vegaanisia annoksia.', 'https://mekong.restaurant/', NULL, 'Lapinrinne 2E', '00180', 'Helsinki', '2022-01-30 17:05:36.78485+02', 10);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (67, 'Kulosaaren Casino', 'Vain 10 minuutin matkan päässä Helsingin keskustasta, Kulosaaren Casino on perinteinen ja tasokas ravintola Helsingin kauneimmassa merimaisemassa.  Vuonna 1915 perustettu ravintola on tunnettu hyvästä ruuastaan ja ainutlaatuisesta tunnelmastaan. Ravintola on myös kaupungin suosituimpia juhlapaikkoja - suurille ja pienille seurueille.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (4289, 'La Bella Trattoria', 'Kalasatamassa sijaitsevan italialaisen La Bella Trattorian ruokalistalta löytyy kaikki italialaiset perinneruoat. Pastat lukuisine eri vaihtoehtoineen, huolella valmistetut risotot, pizzat ja salaatit. Listalla on myös kala-, kana- ja liharuokia.', 'https://www.labellatrattoria.fi/', NULL, 'Capellan puistotie 2', '00540', 'Helsinki', '2022-01-30 17:05:36.704712+02', 25);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2001, 'Kuurna', 'Kuurna on pieni ja viihtyisä ravintola Kruununhaassa. Menu vaihtuu kahden viikon välein, jonka lisäksi ravintola tarjoaa muutamia päivittäin vaihtuvia annoksia. Raaka-aineet ovat tarkoin valikoituja ja menu koostuu pääosin suomalaisista klassikoista, joihin on lisätty moderneja elementtejä.', 'https://www.kuurna.fi', NULL, 'Meritullinkatu 6', '00170', 'Helsinki', '2022-01-30 17:05:36.708014+02', 20);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3931, 'Käpygrilli', 'Käpygrilli on Käpylässä sijaitseva perinteinen kuppila jossa perjantai-iltaisin live-musiikkia ja lauantaisin karaokea. Viikon ohjelmatarjontaan kuuluvat lisäksi myös mm. tietovisat, bingoillat ja open stage jamit. 
