INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2541, 'Löyly', 'Ville Haran ja Anu Puustisen suunnittelema, vuonna 2016 avattu Löyly on ainutlaatuinen niin sijainnin kuin arkkitehtuurin puolesta. Rakennuksessa toimii ravintola, terassi ja kolme saunaa. Kaikki kolme saunaa ovat puulämmitteisiä: jatkuvalämmitteinen sauna, kertalämmitteinen sauna ja Helsingin ainoa yleisölle avoin savusauna. Yhdellä käynnillä voi siis kokea molemmat perinteiset löylytyypit. Saunojen väliin jää takkahuone, jossa voi levähtää saunomisen lomassa. Saunoista pääsee vilvoittelemaan terassille ja uimaan mereen ympäri vuoden. Miehille ja naisille on omat pukuhuoneet ja suihkut, mutta saunat ja oleskelutilat ovat yhteiset, joten yleisessä saunassa uima-asu on pakollinen.
