INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (1443, 'Maya Bar & Grill', 'Maya Bar & Grill sijaitsee Rautatieaseman tuntumassa, Kansallisteatterin vieressä. Mayassa tarjoillaan autenttisia makuelämyksiä latinalais-amerikkalaiseen tyyliin joka päivä, niin lounaan kuin illallisenkin muodossa. Tunnelmaan voi heittäytyä nauttimalla aitoja latinodrinkkejä aina Pisco Sourista Caipirinhaan. Valikoimasta löytyy myös meksikolaista pienpanimo-olutta.', 'https://www.maya.fi', NULL, 'Mikonkatu 18', '00100', 'Helsinki', '2022-01-30 17:05:36.7776+02', 30);
