INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2615, 'Ônam', 'Ônam on vietnamilainen ruokakonsepti, joka tarjoaa asiakkailleen aitoa vietnamilaista ruokaa suoraan katukeittiöstä. Ruokafilosofiaan kuuluu kevyitä ja terveellisiä annoksia, jotka on valmistettu perheresepteillä. Gluteenittomia annoksia saatavilla ja vegaanisia vaihtoehtoja löytyy paljon. Ônamissa tarjoillaan herkullisen pho-keiton lisäksi katuruokaklassikoita kuten goi cuon kesärullia, banh xeo, bun cha, bun bo hue sekä vietnamilaista kahvia ja jälkiruokia.', 'http://www.onam.fi', NULL, 'Mannerheimintie 14, Kauppakeskus Forum', '00100', 'Helsinki', '2022-01-30 17:05:36.829113+02', 10);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2757, 'Onda', 'Onda on ravintola, joka tarjoaa käsintehtyä, maukasta maailmanruokaa. Ruoka on suurimmaksi osaksi vegaanista, mutta listalta löytyy myös liha- ja kalaruokia. Arkisin tarjolla suosittu lounas, iltaisin annoksia ja lauantaisin perulainen brunssi. Ravintola sijaitsee Siltasen yhteydessä. Ravintola on avoinna klo 21/22 saakka, jonka jälkeen Siltanen jatkaa muuttuvilla musiikkitapahtumillaan myöhään yöhön.', 'https://www.ondaruokala.fi/', NULL, 'Hämeentie 13 B, Siltanen', '00530', 'Helsinki', '2022-01-30 17:05:36.831162+02', 25);
