INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3440, 'Pizzeria Luca', 'Pizzeria Luca on syntynyt halusta viettää aikaa asiakkaiden kanssa. Rupatella, nauraa, innostua ja rentoutua. Pizzeria Lucassa voi nauttia aidon napolilaistyylisen pizzan ja siemailla sen kanssa upeaa viiniä. Ravintola haluaa myös ilahduttaa vieraitaan tarjoilemalla vaihtuvia erikoismakuja. 
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2484, 'Pjazza', 'Pjazza on Helsingin keskustassa sijaitseva italialainen ravintola, jossa yhdistyy rakkaus italialaiseen ruokaan ja jazz-musiikkiin. Pjazzan ruokalista rakentuu Keski-Italian perinteisen ruokakulttuurin ympärille. Pizzan tekemisessä noudatetaan alkuperäistä reseptiä, ja taikinaa kohotetaan vähintään 72h.', 'http://pjazza.fi/', NULL, 'Yrjönkatu 18 B', '00120', 'Helsinki', '2022-01-30 17:05:36.87782+02', 10);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3722, 'Platta', 'Messukeskuksen yhteydessä sijaitsevan ravintola Plattan kodikas sisustus yhdistettynä teollisiin elementteihin luovat mielenkiintoisen ja kutsuvan ympäristön nauttia niin lounasta kuin illallista yhdessä tai yksin. 
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3174, 'Pobre', 'Ensimmäinen ravintola Suomessa, joka tarjoaa modernia filippiiniläistä ruokaa. Pobre sai alkunsa vuonna 2013 Ravintolapäivänä, ja on sen jälkeen avannut oman ravintolan Kampin kauppakeskuksen 5.kerrokseen.', 'https://www.pobre.fi/', NULL, 'Urho Kekkosen katu 1, Kampin kauppakeskus 5.krs', '00100', 'Helsinki', '2022-01-30 17:05:36.882872+02', 10);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3194, 'Pontus', 'Ullanlinnalainen korttelibistro ja viinibaari entisen KOM-ravintolan tiloissa jossa ruokalistan runko rakentuu kiviuunin ympärille, tarjolla on siis mm. uuniruokia, pizzoja ja muita hyvien viinien pariksi sopivia isoja sekä pieniä annoksia.', 'https://ravintolapontus.fi/', NULL, 'Kapteeninkatu 26', '00140', 'Helsinki', '2022-01-30 17:05:36.885475+02', 29);
