INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (1172, 'Pompier Espa', 'Pompier Espa sammuttaa nälän ja janon lounasaikaan arkisin ja tiistaista lauantaihin on tarjolla a la carte -menu klo 17 alkaen. Lounaalla lämmin ruoka tarjoillaan lautasannoksina pöytään, illallinen tarjoillaan listalta jonka valikoima on korkealaatuinen ja tunnelma rento. Valikoimasta löytyy mm. jo klassikoksi muodostunut Tarte Tatin, jota on tarjottu samassa osoitteessa ja samalla reseptillä jo vuodesta 2006 alkaen.', 'https://pompier.fi/espa/', NULL, 'Eteläesplanadi 8', '00130', 'Helsinki', '2022-01-30 17:05:36.890547+02', 49);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3322, 'Pub Peräkammari', 'Kahden siskoksen rennon tyylikäs Pohojalaashenkinen pubi jossa voi pelata lautapelejä, dartsia, biljardia, lukea päivän lehdet tai vain keskittyä nauttimaan juomasta. Laaja valikoima erikoisoluita joka lähtöön. Pubiruokaa tarjoileva keittiö on auki aina sulkemiseen saakka. Perjantaisin ja lauantaisin livemusiikkia.', 'https://perakammari.fi/', NULL, 'Eerikinkatu 2', '00100', 'Helsinki', '2022-01-30 17:05:36.893063+02', 10);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (4266, 'Pretty Boy Wingery', 'Pretty Boy Wingery on wingseihin ja kansainvälisiin makuihin erikoistunut, rohkeasti erilainen ravintola. Laadukkaista raaka-aineista loihditut annokset ovat keränneet makupalettiaan maailman eri kolkista. Kaikkea överi-herkullista kanasta, maukasta NoChickeniä unohtamatta!', 'https://www.prettyboy.fi/', 'https://tprimages.blob.core.windows.net/public/4266/daf62044-ff0c-4eb4-924a-a8d8aaf060ae.jpg', 'Töölöntori', '00260', 'Helsinki', '2022-01-30 17:05:36.895603+02', 16);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3236, 'Pueblo Bar y Taqueria', 'Aito meksikolainen avokeittiö ja baari. Suomen ensimmäinen taqueria ja baari, jossa tarjolla on päivittäin leivottuja ja paistettuja tortilloja sekä tuoreista raaka-aineista käsin tehtyjä salsoja sekä ruoka-annoksia. Tarjolla myös meksikolaisen ja latinalaisen amerikan cocktaileja sekä tuontioluita ja tequilaa.', 'https://pueblobar.fi/', NULL, 'Eerikinkatu 27', '00180', 'Helsinki', '2022-01-30 17:05:36.898148+02', 10);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (391, 'Puotilan kartano', 'Itä-Helsingin merellisessä miljöössä sijaitseva historiallinen Puotilan Kartano kutsuu nauttimaan tasokkaista makuelämyksistä, lämminhenkisestä kartanotunnelmasta sekä mieleenpainuvista tapahtumista. Kartanossa herkutellaan viikon mittaan maukkailla lounailla, tyylikkäillä a la carte -annoksilla sekä viikonloppuisin tarjolla olevalla brunssilla. Viehättävä kartano toivottaa tervetulleeksi joko yksin tai yhdessä isomman seurueen kanssa.', 'https://www.puotilankartano.fi/', 'https://tprimages.blob.core.windows.net/public/391/82ce2571-5714-4834-925c-df7fe1f98e2d.jpg', 'Puotilantie 7', '00910', 'Helsinki', '2022-01-30 17:05:36.900715+02', 26);
