INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2306, 'Putte''s Bar & Pizza', 'Putte''s Bar and Pizza eli "Putte" on jo nyt yksi Helsingin klassikkobaareista. Pizzojen salaisuus on niiden yksinkertaisuus. Resepti löydettiin pitkän tutkimusmatkan jälkeen New Yorkista. Putte on lapsiystävällinen baarin ja ravintolan sekoitus, jonka keittiö on auki poikkeuksellisen myöhään.
