INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3490, 'Ragu', 'Skandinaavisen keittiön puhtaudesta ja kekseliäisyydestä inspiroitunut ravintola jonka intohimona ovat Italian ruokaperinteet, viinit ja upeat raaka-aineet.', 'https://www.ragu.fi/', NULL, 'Ludviginkatu 3-5', '00130', 'Helsinki', '2022-01-30 17:05:36.922795+02', 49);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (1030, 'Ravintola Kuu', 'Töölönkadulla sijaitseva Ravintola Kuu on tarjonnut ravintolaelämyksiä jo vuodesta 1966. Kuun nuorekas ja innokas keittiötiimi valmistaa suomalaisen ja skandinaavisen keittiön klassikkoja uudella innovatiivisella ja modernilla otteella.', 'https://ravintolakuu.fi/', NULL, 'Töölönkatu 27', '00260', 'Helsinki', '2022-01-30 17:05:36.925488+02', 16);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (609, 'Ravintola Aino', 'Ravintola Ainon tavoitteena on luoda rehellisiä makuja kauden parhaista raaka-aineista. Ruokalistaan haetaan innoitusta kotimaisista raaka-aineista sekä suomalaisista perinneruuista. Ravintola sijaitsee Töölössä, Kansallisoopperaa vastapäätä.', 'http://www.ravintolaaino.fi/', NULL, 'Mannerheimintie 56', '00260', 'Helsinki', '2022-01-30 17:05:36.927986+02', 16);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (1603, 'Ravintola Aito', 'Ravintola Aito on pieni intiimi ja rennon tyylikäs töölöläisten ja muiden kaupunkilaisten ruoka- ja kohtauspaikka. Ravintola Aito tarjoaa aitoa, konstailematonta ja modernia sekä perinteistä, kotimaisiin raaka-aineisiin luottavaa ruokaa kansainvälisiä tuulahduksia unohtamatta.', 'http://ravintola-aito.fi/', NULL, 'Museokatu 29', '00100', 'Helsinki', '2022-01-30 17:05:36.930438+02', 28);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (4146, 'Ravintola Makalu', 'Ravintola Makalu on nepalilainen ravintola Malmilla jonka monipuoliselta á la carte -listalta löytyy niin kasvis-, kana-, lammas-, liha- kuin kalaruokia.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3401, 'Ravintola Ani', 'Vuonna 1982 perustettu Helsingin vanhin turkkilainen ravintola.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (4067, 'Ravintola Annan Kartano', 'Ravintola Annan Kartano on ainutlaatuinen ravintola, kahvila, kokous- ja juhlatila upeassa kartanomiljöössä Tuomarinkylän kartanon pihapiirissä.', 'https://www.annankartano.fi/', NULL, 'Tuomarinkyläntie 18', '00690', 'Helsinki', '2022-01-30 17:05:36.937882+02', 42);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (4127, 'Ravintola Arbailo', 'Arbailo on ravintola Puhoksen kauppakeskuksessa jossa tarjolla mm. hiiligrillissä valmistettua shish kebabia.', 'https://fi-fi.facebook.com/ArbailoRavintola/', NULL, 'Kastelholmantie 2', '00900', 'Helsinki', '2022-01-30 17:05:36.94025+02', 26);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (470, 'Ravintola Arthur', 'Ravintola Arthur Helsinki on toiminut vuodesta 1927, jolloin avattiin siihen aikaan Hospizina tunnetun hotellin suuren suosion saavuttanut ruokala. Nykyisellä paikallaan Ravintola Arthur on toiminut vuodesta 1957.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3064, 'Ravintola Backas', '200 vuotta vanhassa kartanomiljöössä, kakluuneineen sekä puistoineen, sijaitseva Ravintola Backas toimii pääasiassa tilausravintolana. Ravintola Backaksen pääideana on tarjota kaikille laatua luonnosta lautaselle.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (578, 'Ravintola Bellevue', 'Bellevue on Helsingin ja Suomen vanhin venäläinen ravintola, perustettu vuonna 1917. Ravintola sijaitsee Katajanokalla, aivan Uspenskin katedraalin kupeessa. Bellevue on hakenut inspiraatiota juuriltaan tsaarinajan ranskalaisen keittiön ja slaavilaisten vaikutteiden cocktailista tämän päivän vieraille. Bellevue on matalan kynnyksen olohuone ja ruokasali, jossa voi nauttia niin päivällisannoksen kuin juhlavan illallisen. Baarin puolella voi valita zakuska-palan tai muutaman lasillisen seuraksi.', 'https://www.restaurantbellevue.com/', NULL, 'Rahapajankatu 3', '00160', 'Helsinki', '2022-01-30 17:05:36.947377+02', 52);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (753, 'Ravintola Blue Peter', 'Blue Peter on Lauttasaareen vuonna 1976 perustettu merenrantaravintola, joka palvelee asiakkaita ympärivuotisesti. Keittiö tarjoilee konstailemattomia bistroannoksia kansainvälisen keittiön otteella unohtamatta suomalaisia raaka-aineita ja sesonkeja. Saapuminen on helppoa niin maitse kuin meriteitse.', 'https://bluepeter.fi/', NULL, 'Vattuniemen puistotie 1', '00210', 'Helsinki', '2022-01-30 17:05:36.95+02', 14);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (4149, 'Ravintola Borneo', 'Lounasravintola Borneo tarjoaa autenttista maleasialaista kotiruokaa. Kaikki reseptit ovat peräisin ravintoloitsijan äidiltä ja hänen suvultaan - niin kotikeittiöistä kuin perheen omasta ravintolasta Maleasiassa.', 'https://www.ravintolaborneo.fi/', NULL, 'Itälahdenkatu 20', '00210', 'Helsinki', '2022-01-30 17:05:36.952523+02', 14);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2746, 'Ravintola Bröd', 'Ravintola Bröd on urbaani bistro, jonka ruokatuote on sekoitus skandinaavisuutta, eurooppalaisuutta ja helsinkiläisyyttä. 
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (904, 'Ravintola Carelia', 'Ravintola Carelia on viihtyisä ranskalainen bistro Helsingissä, jossa ranskalaiset vaikutteet ja vanhan ajan tunnelma kohtaavat. Rento brasserie-ilmapiiri uniikissa vanhassa apteekkimiljöössä, jossa on edelleen tallella 1920-luvulla toimineen apteekin alkuperäinen sisustus, kutsuu viihtymään sekä nauttimaan hyvästä ruoasta ja juomista. Ravintola sijaitsee Kansallisoopperaa vastapäätä. Keskeisen sijainnin vuoksi ravintolaan on luonteva jatkaa illalliselle tai lasilliselle oopperanäytöksen jälkeen tai muuten vain kesken päivän. Arkipäivisin tarjolla on lounas ja iltaisin monipuolinen à la carte ja laaja valikoima oman viinimaahantuonnin viinejä.', 'https://ravintolacarelia.fi/', NULL, 'Mannerheimintie 56', '00260', 'Helsinki', '2022-01-30 17:05:36.957654+02', 16);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2347, 'Ravintola Casa Mare', 'Casa Mare on Lauttasaaren sydämessä sijaitseva ruokaravintola, joka tarjoaa asiakkailleen huolella tehtyä mutta rentoa ruokaa mukavan miljöön ja iloisen palvelun siivittämänä. Herkulliset lounas- ja à la carte-annokset sekä mainiot juomat vievät kielen mennessään. Lapsiystävällinen ravintola huomioi perheen pienimpiä omalla ruokalistalla ja leikkinurkalla.', 'https://www.ravintolacasamare.com/', 'https://tprimages.blob.core.windows.net/public/2347/6d694be4-b4ba-4984-9f12-f33a5b199be0.jpg', 'Gyldénintie 6', '00200', 'Helsinki', '2022-01-30 17:05:36.960306+02', 14);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (1130, 'Ravintola Central Park', 'Ravintola Central Parkin ruokafilosofia pohjautuu skandinaaviseen puhtauteen ja tuoreuteen. Listalta löytyy myös kansainvälisiä annoksia kivalla pohjoismaisella twistillä.  Tämän päivän ja tulevaisuuden hyvää oloa kuvaaviin skandinaavisiin termeihin kiteytyy niin ravintola Central Parkin kuin koko hotellinkin liikeidea. Näihin molempiin liittyy skandinaavisuus, kotoisuus, rento ja hyvä fiilis, tasapaino, puhtaat raaka-aineet kuin terveellisyyskin.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (1102, 'Ravintola Demo', 'Ravintola Demo on Helsingin Uudenmaankadulle vuonna 2003 perustettu fine dining -ravintola. Antaumuksellinen käsityönä tehty ruoka, näyttävät annokset ja persoonallinen palvelu on palkittu vuodesta 2007 asti Michelin tähdellä. Demon omistajan ja keittiömestari Tommi Tuomisen omistautuminen ravintolalle näkyy jatkuvana korkeana laatuna ravintolan keittiön työskentelyssä. Ravintolan tarjoilee päivän menua, joka sisältää joko kuusi tai seitsemän ruokalajia, ja joka rakentuu sesongin ja päivän raaka-aineiden pohjalta.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3787, 'Ravintola Famu', 'Ravintola Famussa nautitaan pohjoismaisesta ja eurooppalaisesta ruokakulttuurista, joiden parhaat klassikkoreseptit on päivitetty moderneiksi kokonaisuuksiksi ja jotka yllättävät raikkaalla esillelaitolla.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3824, 'Ravintola Farouge', 'Eksoottinen, maukas ja aito libanonilainen ravintola Farouge toi välimerellisen ruokakulttuuri Helsinkiin jo vuonna 1995. 
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3222, 'Ravintola Emo', 'Modernia eurooppalaista ruokaa pohjoismaisella otteella. Lisäksi ravintolassa on cocktailbaari ja kesäterassi.', 'https://emo-ravintola.fi/', 'https://tprimages.blob.core.windows.net/public/3222/43779ac7-5413-4c63-be9a-0e09f112ede1.jpg', 'Kasarmikatu 44', '00130', 'Helsinki', '2022-01-30 17:05:36.971455+02', 49);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3489, 'Ravintola Gandhi', 'Ravintola Gandhi on Helsingissä toimiva intialainen ravintola, joka tarjoilee huolella valikoituja suomaiseen makuun sopivia ruokalajeja.', 'https://www.gandhi.fi/', NULL, 'Toivonkatu 1-3', '00250', 'Helsinki', '2022-01-30 17:05:36.973265+02', 16);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2669, 'Ravintola Oiva', 'Ravintola Oiva, aitoa stadilaista ravintolakulttuuria Kalliossa lähes 80 vuoden ajan. Arkisin Oivassa nautitaan herkullista lounasta noutopöydästä, iltakeittiö palvelee ti-la ja viikonloppuisin tarjolla on brunssia. Oivassa on lähes joka ilta tarjolla ohjelmaa: on karaokea, Open Stagea, live-klubia ja superbingoa. Baarin tarjonnasta löytyy mm. erinomainen olutvalikoima.', 'https://www.ravintolaoiva.fi', NULL, 'Porthaninkatu 5', '00530', 'Helsinki', '2022-01-30 17:05:37.046639+02', 36);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (1078, 'Ravintola Graniittilinna', 'Graniittilinna on tunnettu maukkaista ja herkullisista ruoistaan, ensiluokkaisesta palvelustaan sekä tunnelmallisesta miljööstään. Keittiö käyttää korkealaatuisia raaka-aineita jotka valitaan ja valmistetaan huolella ja rakkaudella. Tunnelmallisen ravintolan sydän on aito puuhiiligrilli, jonka avulla raaka-aineiden ominaisaromit tuodaan esiin parhaimmalla tavalla - mehukkuudesta tinkimättä. Ruokalista tarjoaa runsaasti valinnanvaraa niin lihan, kalan kuin kasvisten ystäville.', 'https://www.graniittilinna.com', 'https://tprimages.blob.core.windows.net/public/1078/39aae90e-2fff-4bb2-af40-9b000f62e6de.jpg', 'Säästöpankinranta 6', '00530', 'Helsinki', '2022-01-30 17:05:36.974866+02', 36);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3627, 'Ravintola Happy Friends', 'Happy Friendsin lounas yhdistää cross kitchen-hengessä makuja maailmalta ja Suomesta.', 'https://www.happyfriends.fi/', NULL, 'Sinikalliontie 7', '02630', 'Espoo', '2022-01-30 17:05:36.976463+02', 31);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3633, 'Ravintola Hausman', 'Ravintola Hausmanin on lämminhenkinen bistrotyylinen ravintola jonka ruokalista edustaa saksalaista -italialaista keittiötä. Listalta löytyy muun muassa erilaisia pasta-annoksia ja badenilaiseen tapaan valmistettua maksaa. Hausmanniin voit tulla pikaiselle lounaalle, piipahtaa lasilliselle tai illallistaa pitkän kaavan mukaan. Kesäaikaan ravintolan edessä on mukava terassi.', 'http://ravintolahausman.fi/', NULL, 'Töölönlahdenkatu 3', '00100', 'Helsinki', '2022-01-30 17:05:36.978528+02', 30);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3812, 'Ravintola Hefe', 'Lounasravintola', 'https://www.facebook.com/Ravintola-Hefe-155362528455682/', NULL, 'Henry Fordin katu 6 B', '00150', 'Helsinki', '2022-01-30 17:05:36.980643+02', 18);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (4233, 'Ravintola Makasiini', 'Scandic Grand Marinan ravintola Makasiini poimii parhaat katuruokatrendit ja yhdistelee luovasti terveellisiä ja hyvälaatuisia proteiineja sekä raikkaita kasviksia.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (1280, 'Ravintola Grotesk', 'Ravintola Grotesk on tarjoillut makuelämyksiä Erottajalla jo yli vuosikymmenen. Groteskin puuhiiligrillissä täydelliseksi paahtuvat niin lihat, meren herkut kuin kauden kasviksetkin. Listalta löytyy herkkuja jokaiseen makuun. Kaikki, leivästä jäätelöön, valmistetaan käsin alusta loppuun parhaista raaka-aineista.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (92, 'Ravintola Hong Kong', 'Ravintola Hong Kong on viihtyisä kiinalainen ravintola Taka-Töölössä Olympiastadionin vieressä.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3347, 'Ravintola il Bucatino', 'Ravintola Trattoria Il Bucatino on 40-paikkainen italialaisravintola Eirassa jonka toiminnan lähtökohtana on perustajiensa, kolmen italialaismiehen, näkemys aidosta italialaisesta ruuasta nykyaikaisessa trattoriassa.', 'https://www.ilbucatino.com/', NULL, 'Tehtaankatu 38', '00150', 'Helsinki', '2022-01-30 17:05:36.988504+02', 48);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3041, 'Ravintola Ilves', 'Vuonna 1932 ovensa avannut Ravintola Ilves on aina tarjonnut rehtiä ruokaa.  Sesongin mukaan vaihtuvalla, kohtuuhintaisella ruokalistalla on parhaista ja tuoreista aineksista valmistettuja à la carte -annoksia, jotka tarjoillaan pöytään lämminhenkisellä ja asiantuntevalla palvelulla. Juomatarjontaan kuuluvat mm. hyvä kokoelma oluita niin pienpanimoilta kuin suuremmilta tekijöiltä sekä kiinnostava ja monipuolinen valikoima persoonallisia luomuviinejä pientuottajilta ympäri maailman. Ilveksessä voi nauttia sekä lounasta että illallista tai poiketa lasillisille vaikkapa ennen illan keikkaa naapurissa sijaitsevalla Tavastiaklubilla - tai sen jälkeen.', 'https://www.ravintolailves.fi/', NULL, 'Urho Kekkosen katu 4-6', '00100', 'Helsinki', '2022-01-30 17:05:36.990147+02', 10);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3009, 'Ravintola Jura', 'Ravintola Jura on pieni ja mutkaton ravintola Ullanlinnan sydämessä. Keittiössä sesonkeja vaalitaan ja menu elää jatkuvasti niiden mukana. Sekä á la cartessa että neljän ruokalajin menussa kasvikset ovat paraatipaikalla ja viinikaapin persoonalliset antimet täydentävät illalliskokemuksen huippuunsa.', 'https://www.ravintolajura.fi/', NULL, 'Neitsytpolku 10', '00140', 'Helsinki', '2022-01-30 17:05:36.991875+02', 29);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (1533, 'Ravintola Pihlajasaari', 'Suosittu ulkoilusaari Pihlajasaari sijaitsee Helsingin edustalla, vain 10 minuutin merimatkan päässä Kaivopuiston rannasta. Saaren A-oikeuksin varustettu kahvila-ravintola on palvellut saaressa kävijöitä vuodesta 1929 asti Villa Hällebossa, joka rakennettiin alunperin huvilaksi jo vuonna 1883.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2730, 'Ravintola Kaarti', 'Vanhan Kaartin Pataljoonan tiloissa sijaitseva ravintola jossa kunnioitetaan ja korostetaan rakennuksen vanhan ajan henkeä luomalla tilaan nostalgista fiilistä niin ravintolan somistuksen kuin henkilökunnan vaatetuksen myötä. Nykyaikaa ravintolassa edustaa trendikäs sisustus ja ruokatarjonta. Ravintola Kaartissa nautitaan runsaasta lounaasta ja monipuolisesta brunssista. Iltaisin Kaarti toimii tilausravintolana.', 'https://www.ravintolakaarti.fi/', NULL, 'Pohjoinen Hesperiankatu 33 A', '00260', 'Helsinki', '2022-01-30 17:05:36.995464+02', 16);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3074, 'Ravintola Kamome', 'Ravintola Kamome tuo yhteen suomalaisen perinteisen ruuan ja japanilaisen keittiön. Ravintolan tiloissa (silloin vielä Kahvila Suomi) kuvattiin vuonna 2006 suuren menestyksen saanut japanilainen elokuva Kamome Shoduko (Ruokala Lokki).', 'http://www.kamome.fi/', NULL, 'Pursimiehenkatu 12', '00150', 'Helsinki', '2022-01-30 17:05:36.997214+02', 39);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3436, 'Ravintola Kannas', 'Vuonna 1939 perustetussa Kannaksessa viihtyivät aikoinaan merimiehet, ahtaajat ja muut Hietalahden sataman työläiset. Nykyään Kannas tarjoaa asiakkailleen reilua ruokaa ja juomaa värikästä historiaansa unohtamatta.', 'https://www.kannas.fi/', NULL, 'Eerikinkatu 43', '00180', 'Helsinki', '2022-01-30 17:05:36.998925+02', 10);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2873, 'Ravintola Karljohan', 'Karljohan valmistaa klassikoita ja perinneruokia suurella sydämellä. Talon vorschmack, ylikypsähäränrinta, kateenkorva ja muut sisäelinruoat ovat löytäneet yli 30 vuodessa monta uskollista ystävää.', 'http://www.ravintolakarljohan.fi', NULL, 'Yrjönkatu 21', '00100', 'Helsinki', '2022-01-30 17:05:37.000731+02', 10);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2043, 'Ravintola Wanha Mylly', 'Idyllinen ravintola Wanha Mylly sijaitsee 1700-luvulla rakennetussa pehtoorin tuvassa Herttoniemen kartanon mailla. Se on toiminut ruoka-ja seurusteluravintolana jo yli neljäkymmentä vuotta. Kesäkaudella herkutellaan myös ravintolan isolla terassilla syreenipensaiden katveessa', 'https://vanhamylly.fi/', NULL, 'Linnanrakentajantie 12', '00810', 'Helsinki', '2022-01-30 17:05:37.128148+02', 3);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (581, 'Ravintola Kiltakellari', 'Vuonna 1885 rakennetun Sinebrychoffin panimorakennuksen tiloissa toimiva tunnelmallinen ravintola Kiltakellari on sekoitus perinteitä ja modernia muotoilua. Hauska yksityiskohta ravintolasalissa on Koffin hevosten tirkistysaukko. Ravintola toimii vanhassa Sinebrychoffin panimorakennuksessa, jonka sisustuksessa perinteet ja moderni suunnittelu kohtaavat.  Jykevät kattopilarit ja vanhat tiiliseinät luovat vaikuttavan tunnelman. 
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (605, 'Ravintola Kappeli', 'Ravintola Kappeli on suljettu remontin vuoksi ja avaa jälleen toukokuussa 2022. 
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2853, 'Ravintola Kirkuk', 'Puhoksen ostoskeskuksessa Itäkeskuksessa sijaitseva Lähi-idän ruokia tarjoava hiiligrilliravintola.', 'https://fi-fi.facebook.com/ravintolakirkuk/', NULL, 'Kastelholmantie 2, Puhoksen ostoskeskus', '00900', 'Helsinki', '2022-01-30 17:05:37.006019+02', 26);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (481, 'Ravintola Kokki', 'Ravintola Kokki on Stadin Ammattiopiston opetusravintola jossa tarjolla on lounas arkisin. Ravintola Kokin palveluja täydentävät elintarvikealan opiskelijoiden valmistamat leipomo- ja konditoriatuotteet deli-kahvila Kupissa.', 'https://www.hel.fi/ammatillinen/fi/esittely/tuotteet-ja-palvelut/elintarvike-ja-ravintolapalvelut/', NULL, 'Prinsessantie 2', '00820', 'Helsinki', '2022-01-30 17:05:37.007757+02', 3);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (868, 'Ravintola Kolme Kruunua', 'Menneen ajan tunnelmaa, aito 50-luvulta säilynyt interiööri ja perinteistä suomalaista ruokaa kuten esim. lihapullia, pyttipannua, läskisoosia ja paistettuja silakoita.', 'https://www.kolmekruunua.fi/', NULL, 'Liisankatu 5', '00170', 'Helsinki', '2022-01-30 17:05:37.009461+02', 20);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2502, 'Ravintola Koskenranta', 'Koskenranta on Helsingin Vanhankaupunginkosken rannalla sijaitseva idyllinen ravintola. Vuonna 1946 valmistunut entinen Imatran Voiman Vesivirtauslaboratorio toimii tänä päivänä ravintolana ihastuttavassa koskimaisemassa. Ravintola on avoinna ympäri vuoden ja palvelee talvikaudella arkisin lounasaikaan ja viikonloppuisin kahvila-ravintolana. Kesäisin koskea hipovalla terassilla nautit luonnonrauhassa bistro-annoksista ja vaikkapa lasillisesta viiniä.', 'http://www.koskenranta.net', NULL, 'Katariina Saksilaisen katu 9', '00560', 'Helsinki', '2022-01-30 17:05:37.011259+02', 37);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (86, 'Ravintola Koto', 'Japanilainen ravintola Koto on toiminut Helsingissä jo vuodesta 1986. Koto uskoo perinteiseen japanilaiseen keittiöön, puhtaisiin ja korkealaatuisiin raaka-aineisiin.', 'https://ravintola-koto.fi', NULL, 'Lönnrotinkatu 22', '00100', 'Helsinki', '2022-01-30 17:05:37.012973+02', 10);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3875, 'Ravintola Kuninkaan Lohet, Vanha Viilatehdas', 'Ravintola Kuninkaan Lohet sijaitsee kultturihistoriallisesti merkittävällä paikalla, Vantaankosken äärellä. Ravintola tarjoaa buffetlounasta arkipäivisin ja yleisölle avoimia tapahtumia järjestetään ympäri vuoden. Lisätietoa tapahtumista löytyy ravintolan kotisivuilta.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (479, 'Ravintola Lehtovaara', 'Ravintola Lehtovaara on yksi Helsingin tunnetuimmista klassikkoravintoloista. Lehtovaara on läpi vuosikymmenten tullut tunnetuksi korkeatasoisen suomalaisen ja kansainvälisen ruuan tarjoajana. Vuosien mittaan Lehtovaara on saanut myös useita kunnianosoituksia. Ravintolassa on myös suosittu, ympäri vuoden avoinna oleva lasiterassi.', 'https://www.lehtovaara.fi/', NULL, 'Mechelininkatu 39', '00250', 'Helsinki', '2022-01-30 17:05:37.016289+02', 16);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2985, 'Ravintola Lönkka', 'Lönkka on rento olut- & ruokabistro Helsingin keskustassa. Ravintola Lönkan ruoka edustaa modernia eurooppalaista keittiötä. Ravintola tarjoilee konstailematonta ja rehtiä ruokaa sekä sydämellistä ja ystävällistä palvelua, unohtamatta rentoa ja välitöntä tunnelmaa. Tarjolla on niin nopeaa lounasta kuin herkuttelua illallisella kahdestaan tai suuremmalla seurueella.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3794, 'Ravintola Kuusi Palaa', 'Ravintola Kuusi Palaa Hotelli Marskissa tarjoaa suomalaisia makuja modernilla twistillä. Se liputtaa puhtaiden suomalaisten raaka-aineiden ja yhdessä syömisen puolesta. Ravintola tarjoaa yllätyksiä, oivalluksia ja uusia näkökulmia - kaikkea mitä nykyaikaiseen ravintolailtaan kuuluu. 
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (306, 'Ravintola Lasipalatsi', '30-luvun funkkistyyliin entisöity ravintola on yleisilmeeltään ilmava, valoisa ja selkeä. Ravintola Lasipalatsi tarjoaa suosittujen klassikkoannosten lisäksi kunkin vuodenajan herkkuja silloin kun ne ovat parhaimmillaan. Menussa näkyvä suomalaisuus korostuu myös raaka-aineiden valinnassa.', 'https://www.ravintolalasipalatsi.fi/', 'https://tprimages.blob.core.windows.net/public/306/969c9c11-ca51-43ac-ba7b-4ebc46fc54a1.jpg', 'Mannerheimintie 22-24', '00100', 'Helsinki', '2022-01-30 17:05:37.02128+02', 10);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3213, 'Ravintola Makuja', 'Ravintola Makuja on Kampin kupeessa sijaitseva terveystietoinen sushiravintola, jossa tarjoillaan herkullisia makuelämyksiä niin perinteisen kuin vegaanisen sushin muodossa. Sushibuffet on auki aamusta iltaan ja tarjolla on myös valmisannoksia.', 'https://ravintolamakuja.fi/', NULL, 'Fredrikinkatu 46', '00100', 'Helsinki', '2022-01-30 17:05:37.023013+02', 10);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3120, 'Ravintola Más', 'Narinkkatorilla sijaitseva Más on inspiroitunut Iberian niemimaan modernista keittiöstä, pohjoismaisesta ruokatyylistä ja lähellä tuotetuista laadukkaista raaka-aineista. Ravintolassa voi maistella useita pieniä annoksia, kokeilla avokeittiön Rotisserie-grillin herkullisia makuja tai tilata isomman annoksen jaettavaksi koko seurueen kesken. Ravintola Más palvelee lounaasta illalliseen. Ravintolan Narinkkatorin ylle avautuvalla terassilla pitkä ilta hyvässä seurassa vaihtuu nopeasti yöhön.', 'https://www.scandichotels.fi/mas', 'https://tprimages.blob.core.windows.net/public/3120/5ab563b9-dbfa-4963-b526-8ac347195264.jpg', 'Simonkatu 9, Scandic Simonkenttä', '00100', 'Helsinki', '2022-01-30 17:05:37.024777+02', 10);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2535, 'Ravintola Weeruska', 'Weeruska on lämminhenkinen kortteliravintola Helsingin Alppilan sydämessä. Weeruskan fiilis syntyy kahdesta päätekijästä, jotka ovat reilu ruoka ja rento palvelu. Weeruska on avoinna joka päivä lounaasta illalliseen.', 'https://www.weeruska.com/', 'https://tprimages.blob.core.windows.net/public/2535/56a3501f-f2de-4174-87d2-643d707bf1ff.jpg', 'Porvoonkatu 19', '00510', 'Helsinki', '2022-01-30 17:05:37.129922+02', 2);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (4213, 'Ravintola Merimakasiini', 'Ravintola Merimakasiini on kaikille meren, merielämän ja meren herkkujen ystäville. Tämä Hietalahti Portin raikas merellinen helmi Hietalahdenrannassa tarjoaa aina sesongin parhaat ja tuoreimmat kala- ja äyriäisruuat, kasvis- ja lihavaihtoehtoja unohtamatta, viihtyisässä satamaympäristössä keskustan tuntumassa aivan Hietalahden torin läheisyydessä. Valitse suosikkisi listan klassikoista tai anna sesongin erikoisuuksien viedä kielesi mennessään.', 'https://merimakasiini.fi/', NULL, 'Hietalahdenranta 14', '00180', 'Helsinki', '2022-01-30 17:05:37.026775+02', 10);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2476, 'Ravintola Meripaviljonki', 'Meripaviljonki on ainutlaatuinen meressä kelluva panoraamaravintola keskellä Helsinkiä, joka palvelee sekä tyylikkäänä lounas- että illallisravintolana. Meripaviljonki on erikoistunut meren herkkuihin: kaloihin, äyriäisiin ja hummereihin, joita varten salissa on oma allas. Listalta löytyy toki myös kasvis- ja liharuokia. Meripaviljongissa voi nauttia muutaman henkilön illallista tai järjestää teemallisia koko ravintolan kattavia yksityistilaisuuksia.', 'https://www.ravintolameripaviljonki.fi/', 'https://tprimages.blob.core.windows.net/public/2476/899977f0-6924-40c2-9b35-ca4d706eeb22.jpg', 'Säästöpankinranta 6', '00530', 'Helsinki', '2022-01-30 17:05:37.028744+02', 36);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (1273, 'Ravintola Olo', 'Ravintola Olo on moderniin pohjoismaiseen keittiöön erikoistunut ruokaravintola Helsingin keskustassa. Olon keittiö edustaa uutta pohjoismaista keittiösuuntausta, jossa perinteisyyttä edustavat kaikista Pohjolan maista kootut raaka-aineet. Tarjonta on selkeää ja linjakasta, kuten Pohjoismainen muotoilukin ja vuodenajan parhaita raaka-aineita kunnioitetaan saatavuuden mukaan. Maan ja veden omat, luonnolliset ja puhtaat maut hallitsevat niin Olon menukokonaisuuksia. 
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (475, 'Ravintola Meritorppa', 'Meritorppa on Helsingin Munkkiniemessä, Kalastajatorpantiellä sijaitseva merellinen ja viihtyisä rantaravintolahelmi, jonka välitön ja lämmin ilmapiiri sekä asiantunteva palvelu houkuttelevat asiakkaita lounaasta illalliseen ja vuodesta toiseen. Ravintola toimii Hilton Helsinki Kalastajatorppa -hotellin yhteydessä. 
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (903, 'Ravintola Messenius', 'Ravintola Messenius on vuonna 1937 perustettu perinteinen ruokaravintola Helsingin Taka-Töölössä Messeniuksenkadulla, jossa se on toiminut perustamisestaan saakka. Tarjolla on klassikkoannoksia tämän päivän tyyliin.', 'https://www.messenius.fi/', 'https://tprimages.blob.core.windows.net/public/903/2b9e2d7d-1d82-4741-afbb-3b934bbf22b7.jpg', 'Messeniuksenkatu 7', '00250', 'Helsinki', '2022-01-30 17:05:37.033046+02', 16);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (307, 'Ravintola Mestaritalli', 'Perinteikäs ravintola Mestaritalli sijaitsee aivan meren rannalla, hyvien kulkuyhteyksien varrella Töölössä, Toivo Kuulan puistossa. Kesäkaudella Mestaritallissa nautitaan maistuvaa lounasta ja sunnuntaisin runsaan brunssipöydän antimista elävän musiikin tahtiin. Ravintola on oiva valinta myös illalliselle ja illanistujaisiin. Mestaritallissa voi järjestää myös yksityistilaisuuksia ja se onkin erittäin suosittu hääpaikka.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (1975, 'Ravintola Muru', 'Ravintola Muru on pieni tunnelmallinen bistro Helsingin keskustassa. Murun ruoka edustaa perinteistä ranskalaista keittiötä pienellä italialaisella twistillä, jossa keskiössä ovat käsityö ja herkät maut. Raaka-aineet ovat tarkoin poimittuja niin kotimaasta kuin ulkomailtakin. Murun filosofia on yksinkertainen. Valitse paras saatavilla oleva raaka-aine, valmista se suorasukaisesti ja viiniä kunnioittaen. Muuta ei tarvita.', 'https://www.murudining.fi/', 'https://tprimages.blob.core.windows.net/public/1975/ad0321fc-80b3-4d2f-aa43-be7272fcdf33.jpg', 'Fredrikinkatu 41', '00120', 'Helsinki', '2022-01-30 17:05:37.036951+02', 10);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (78, 'Ravintola NJK', 'Valkosaaren vuonna 1900 rakennettu huvilatyyppinen ravintola NJK (Nyländska jaktklubben) on kesäkauden parhaita juhlapaikkoja, suosittu rapuravintola ja tunnelmallinen ympäristö maukkaalle aterialle. Avaraa, korkeaa keskisalia ympäröi valoisa veranta, jonka suurista lasi-ikkunoista avautuu näköala sekä Kauppatorille että Katajanokan suuntaan.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3212, 'Ravintola Nolla', 'Nolla on ravintola, jossa laadukas ruoka ja kestävä kehitys kulkevat käsi kädessä.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3898, 'Ravintola Oklahoma', 'Vantaan Tikkurilassa sijaitsevassa ravintola Oklahomassa tarjoillaan maittavia villin lännen ruokia jotka valmistetaan aina parhaista raaka-aineista ja suomalaiseen makuun. Listalta löytyy myös tutummat grilli-, kasvis- ja kanaruoat sekä paljon muuta. Oklahoman seisovan pöydän lounas on runsas ja monipuolinen.', 'https://www.oklahoma.fi', 'https://tprimages.blob.core.windows.net/public/3898/d317610d-1485-4732-9362-4f196f554c12.jpg', 'Tikkuraitti 15', '01300', 'Vantaa', '2022-01-30 17:05:37.042607+02', 33);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (472, 'Ravintola Nokka', 'Ravintola Nokka, keittiöpäällikkö Ari Ruohon luotsaamana, on Suomen ainoa ravintola, joka tuntee valikoitujen V.I.P. -tuottajiensa (Very Important Producer) tuotantomenetelmät sekä -olosuhteet henkilökohtaisesti. Lautaselle saapuvat sesongin puhtaimmat antimet niin Sallan metsistä kuin pientiloilta ympäri Suomen.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2927, 'Social Burgerjoint Kulmavuorenkatu', 'Master Chef 2012 voittajan Mika Tuomosen burgeripaikka Sörnäisten Kurvissa. Listalta löytyy erilaisia puuhiiligrillissä valmistettuja burgereita kasvis- ja vegaanivaihtoehtoja ja lisukkeita unohtamatta. 
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (841, 'Ravintola Palace', 'Kaupungin kattojen yllä Etelärannassa sijaitseva Palace on aina ollut suomalaisen gastronomian edelläkävijä. Ravintola valmistui Helsingin olympialaisiin vuonna 1952, ja se oli Suomen ensimmäinen Michelin-tähdellä palkittu ravintola vuonna 1987. Tätä nykyä Palacen keittiötä johtaa keittiöpäällikkö Eero Vottonen, jonka tavoitteena on tarjota vierailleen hienostunut mutta yllätyksellinen pohjoismainen ravintolaelämys.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2716, 'Ravintola Palema', 'Ravintola Palema tarjoilee monipuolista ja herkullista lounasta Teurastamolla arkisin klo 11-14. Lounas tarjoillaan lautasannoksina ja listalla on aina kasvis-, kala- ja lihavaihtoehtoja. Palema on auki Teurastamon tapahtumien yhteydessä ja toimii lounasaikojen ulkopuolella tilausravintolana.', 'https://ravintolapalema.fi/', NULL, 'Työpajankatu 2, rakennus 5', '00580', 'Helsinki', '2022-01-30 17:05:37.05365+02', 43);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (583, 'Ravintola Perho', 'Opetusravintola, jossa Perho Liiketalousopiston ravintolaa-alan opiskelijat saavat ensimmäiset asiakaskokemukset sekä salin että keittiön puolella. Ravintolan yhteydessä toimii Perhon pienpanimo joka toimii Perho Liiketalousopiston opetuspanimona.', 'https://www.ravintolaperho.fi/', NULL, 'Mechelininkatu 7', '00100', 'Helsinki', '2022-01-30 17:05:37.055417+02', 28);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (4139, 'Ravintola Pikku Hukka', 'Ravintola Pikku Hukka on herttoniemeläinen kortteliravintola, jonka filosofiassa hyvä ruoka ja hyvä arki kulkevat käsi kädessä. Ravintola toivottaa tervetulleeksi niin lounaalle kuin nauttimaan kiireettömästi iltapäivästä tai vaikka viettämään mukavaa iltaa perheen tai ystävien kesken.', 'https://www.ravintolapikkuhukka.com/', 'https://tprimages.blob.core.windows.net/public/4139/7cd8410a-2124-47a3-8734-8a7530ef0e9d.jpg', 'Kettutie 3', '00800', 'Helsinki', '2022-01-30 17:05:37.057204+02', 3);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (4108, 'Ravintola PLATS', 'Hanasaaressa sijaitseva Ravintola PLATS on tuttavallinen kohtaamispaikkaa, johon kaikki ovat tervetulleita.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2143, 'Ravintola Puro', 'Puro on skandinaavishenkinen ravintola, joka tarjoaa lounaita ja tilaustarjoiluja.  Ravintola sijaitsee Alvar Aallon suunnittelemassa Sähkötalossa Kampissa. Sisustuksessa on paljon Aallolle tyypillisiä materiaaleja ja hänen suunnittelemiaan huonekaluja, kuten valaisimia ja tuoleja. Ravintolan terassi 8. kerroksessa tarjoaa hienot näkymät yli kaupungin kattojen. Iltaisin ja viikonloppuisin Puro toimii tilausravintolana.', 'https://ruoka.palmia.fi/fi/ravintola/ravintola/puro/', NULL, 'Malminrinne 6 E, 8. kerros', '00100', 'Helsinki', '2022-01-30 17:05:37.060737+02', 10);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3487, 'Ravintola Saba', 'Ravintola Saba on monipuolista intialaista tarjoava ravintola. Valikoimassa on intialaisittain nauta-, kana-, kala- ja kasvisruokia.', 'http://www.ravintolasaba.fi/', NULL, 'Hämeentie 7', '00510', 'Helsinki', '2022-01-30 17:05:37.062701+02', 25);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3485, 'Ravintola Purpur', 'Ravintola Purpur on kiehtova elämysmatka Gruusialaiseen makumaailmaan. Näyttelijä ja matkamies Ville Haapasalon ravintolan eloisa tunnelma ja Villen matkoiltaan keräämien reseptien mukaan valmistettu ruoka ovat täydellinen yhdistelmä lämpimään ja iloiseen illanviettoon.', 'https://www.purpur.fi/', 'https://tprimages.blob.core.windows.net/public/3485/6441228a-2c7a-4d4b-8799-7c93163e050c.jpg', 'Rikhardinkatu 4', '00130', 'Helsinki', '2022-01-30 17:05:37.065235+02', 49);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (1205, 'Ravintola Pyysaari', 'Ravintola Pyysaari on Työväen pursiseuran legendaarinen kerhoravintola. Sen ainutlaatuinen tunnelma syntyy sen perinteikkäästä menusta sekä ympäristöstä, johon ajan hammas ei näytä purreen. Upealla terassilla voi ihailla merimaisemaa ja auringonlaskua, tiistai-iltaisin myös purjehduskilpailua. Sisällä on vuosittain vaihtuva taidenäyttely. 
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (746, 'Ravintola Saaga', 'Kaksikerroksinen ravintola Saaga tarjoilee aitoja pohjoisen makuja ja Lapin eksotiikkaa keskellä Helsinkiä. Katutason ravintolasalissa on katettuna näyttävä aito lappilainen venebuffet (joulukuussa joulubuffet). Yläkerrassa tilat jakautuvat useampiin tunnelmallisiin pienempiin saleihin sekä Shamaani-kabinettiin. Sisustuksessa näkyy Lapin taiteilijoiden ja käsityöläisten kädenjälki.', 'https://www.ravintolasaaga.fi/', 'https://tprimages.blob.core.windows.net/public/746/a43e4a09-178c-4d66-8bb9-570fb53d6ba0.jpg', 'Bulevardi 36', '00120', 'Helsinki', '2022-01-30 17:05:37.070589+02', 10);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (76, 'Ravintola Saari', 'Suomalainen ravintola Saari sijaitsee Sirpalesaaressa aivan Helsingin edustalla, vain kivenheiton päässä Helsingin ydinkeskustasta. Saaren ruokalistalla on suomalaisia sekä saaristolaisherkkuja. 
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3232, 'Ravintola Svengi', 'Lounasravintola Business Park Duettossa joka toimii iltaisin ja viikonloppuisin tilausravintolana. Aamiaisella ravintolassa on tarjolla puuro- sekä leipäbaari. Lounasbuffetista löytyy skandinaavisia makuja sekä tuulahduksia maailmalta, monipuolinen kasvislounas sekä runsas salaatti-keittolounas.', 'https://ruoka.palmia.fi/fi/ravintola/ravintola/svengi/', NULL, 'Läkkisepäntie 23, Business Park Duetto', '00620', 'Helsinki', '2022-01-30 17:05:37.102058+02', 41);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (77, 'Ravintola Saaristo', 'Klippan-nimisellä saarella, Kaivopuiston edustalla sijaitseva jugend-tyylinen 120 vuotias pitsihuvila on osa suomalaista kulttuuriperintöä ja olennainen osa maamme pääkaupungin maisemaa. Arkkitehti Selim Lindqvistin suunnittelema, vuonna 1899 valmistunut rakennus ympäristöineen on yksi Helsingin perinteikkäimpiä ja näyttävimpiä ravintoloita. Saariston ruokalista tarjoaa kesän parhaita skandinaavisia makuja ja makuelämystä täydentävät häikäisevän kauniit merimaisemat. Syyskesästä Saaristo täyttyy rapujuhlien viettäjistä.  
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (577, 'Ravintola Saslik', 'Suomen legendaarisin venäläinen ravintola on vaalinut venäläistä ruokakulttuuria sekä tsaarinajan tunnelmaa jo vuodesta 1972. Persoonallisesti sisustettu miljöö, kodikas vieraanvaraisuus ja laadukkaista raaka-aineista valmistetut herkut tarjoavat unohtumattomia elämyksiä kaikille aisteille. Ruokalistalta löytyvät niin venäläisen keittiön klassikkoannokset kuin myös Saslikin omat kestosuosikit. Ravintola tunnetaan bliniensä lisäksi myös harvinaisista karhuannoksistaan, joita on tarjolla ympäri vuoden. Myös juomalistalta löytyy venäläisiä erikoisuuksia.', 'https://www.ravintolasaslik.fi/', 'https://tprimages.blob.core.windows.net/public/577/99649858-3091-44ed-a591-eb0543d2b9bd.jpg', 'Neitsytpolku 12', '00140', 'Helsinki', '2022-01-30 17:05:37.078669+02', 29);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (1177, 'Ravintola Savu', 'Ravintola Savun miljöönä toimii punamultainen hirsirakennus Kruununhaan edustalla Tervasaaressa johon on tieyhteys. Mökkiteemaisesti sisustettu ravintola tarjoaa  suomalaisia sesongin herkkuja ja kesäisin ruokaa voi sulatella mölkkyä pelaten tai tikkaa heittäen. Ravintolasali 80 henkilölle. Kesäisin suuri katettu ulkoterassi.', 'https://www.ravintolasavu.fi/', 'https://tprimages.blob.core.windows.net/public/1177/b03f6466-8b3e-475e-80a5-9959d0aca993.jpg', 'Tervasaarenkannas 3', '00170', 'Helsinki', '2022-01-30 17:05:37.081377+02', 20);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (1529, 'Ravintola Salutorget', 'Helsingin vanhassa empirekeskustassa sijaitseva kaunis Salutorget kutsuu konstailemattomaan tunnelmaan. Ravintolan sykkivä sydän on vanhaan kauppiastaloon rakennettu, upea jugendtyylinen pankkisali jonka eloisassa tunnelmassa voi viihtyä koko päivän; herkullinen lounas, perinteinen englantilainen Afternoon Tea, illallinen perheen tai ystävien parissa. Illan hämärtyessä meren ylle on kadulta helppo piipahtaa kutsuvaan baariin lasilliselle.', 'https://www.salutorget.fi/', NULL, 'Pohjoisesplanadi 15', '00100', 'Helsinki', '2022-01-30 17:05:37.084193+02', 49);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (1068, 'Ravintola Savotta', 'Tukkilaistunnelmainen Savotta Senaatintorin laidalla tarjoaa aitoa ja reilua suomalaista ruokaa ja silmänruokana aitoja vanhoja esineitä menneiltä vuosikymmeniltä ja savotta-ajoilta. 
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (564, 'Ravintola Savoy', 'Ravintola Savoy on kuulunut helsinkiläisen ravintolamaailman ehdottomiin huippuihin 1930-luvulta asti. Marsalkka Mannerheim oli sen tunnetuimpia asiakkaita, ja Marskin suosikit ovat edelleen ruokalistalla, kuten esim. vorschmack. 
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3851, 'Ravintola Skörd', 'Ravintola Skörd on lähiruokaa ja juomaa parhaimmillaan. Keittiössä loihditaan herkullisia makuelämyksiä mahdollisimman läheltä tulevista sesongin raaka-aineista, itse kerätyistä villiyrteistä ja marjoista sekä riistasta, kalasta ja luomukaritsasta. Kaikki ravintolan raaka-aineet ja juomat tulevat Suomesta. Ruokien kanssa tarjoillaan yhteensopivia laadukkaita kotimaisia oluita, siidereitä, marjaviinejä sekä itse tehtyjä marjaliköörejä ja cocktaileja sesongin raaka-aineista.', 'https://skörd.fi', NULL, 'Fredrikinkatu 37', '00120', 'Helsinki', '2022-01-30 17:05:37.091299+02', 10);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (449, 'Ravintola Solvik', 'Ravintola Solvik on Helsingin Aurinkolahdessa sijaitseva ravintola, jossa on mahdollisuus nauttia arkisin lounasta ja maanantaista sunnuntaihin iltaisin herkullisia annoksia. Sunnuntaisin brunssi. 
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2269, 'Ravintola Spis', 'Ravintola Spis on 18-paikkainen pohjoismainen ruokaravintola. Spis valmistaa ruokaa, jossa käytetään mahdollisimman paljon puhtaita, pohjoismaisia raaka-aineita. Ruokalistalla pääosassa ovat kasvikset, mutta listalta löytyy aina myös  kala- ja liha-annos.', 'https://spis.fi/', NULL, 'Kasarmikatu 26', '00130', 'Helsinki', '2022-01-30 17:05:37.09502+02', 49);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3150, 'Ravintola Stansvik', 'Lounas- ja tilausravintola Stansvikin valoisa yhtenäinen ravintolasali tarjoaa seinien sijaan lumoavan merimaiseman näköalaikkunoista. Suuri merenrantaterassi ja historiallinen kartanoalue luovat viihtyisän ja kiireettömän tunnelman arkeen ja juhlaan.', 'https://www.stansvik.fi/', NULL, 'Stansvikintie 1', '00590', 'Helsinki', '2022-01-30 17:05:37.096793+02', 21);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2026, 'Ravintola Sunn', 'Ravintola Sunn sijaitsee Senaatintorin kulmalla sijaitsevassa historiallisen Sunnin talon toisessa kerroksessa. Näkymät Senaatintorille avautuvat kauniisti kaikista valoisista saleista. Sunn on tyyliltään pohjoismainen ravintola ja tarjoilee arkisin lounasta, viikonloppuisin herkullista brunssia sekä iltaisin à la cartea.', 'https://ravintolasunn.fi/', NULL, 'Aleksanterinkatu 26', '00170', 'Helsinki', '2022-01-30 17:05:37.098525+02', 20);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (204, 'Ravintola Suomenlinnan Panimo', 'Ravintola Suomenlinnan Panimo sijaitsee 15 minuutin merimatkan päässä Helsingin Kauppatorin rannasta, heti Suomenlinnan päälaiturin edessä. Koe ainutlaatuinen Suomenlinnan tunnelma panimoravintolan oluiden ja herkullisen ruoan kera. Ruokalistalta löytyy vaikutteita saaren venäläiseltä ja ruotsalaiselta aikakaudelta tuoreita suomalaisia raaka-aineita painottaen.', 'https://www.panimoravintola.fi/', 'https://tprimages.blob.core.windows.net/public/204/d28a4825-06f8-4052-919e-deeb4da4b79f.jpg', 'Suomenlinna C 1, Rantakasarmi', '00190', 'Helsinki', '2022-01-30 17:05:37.100355+02', 44);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (79, 'Ravintola Särkänlinna', 'Ravintola Särkänlinna sijaitsee linnakerakennuksessa Särkän saarella, josta on näkymät Helsingin suuntaan ja avomerelle. Särkänlinna on yksi Helsingin rakastetuimpia kesäravintoloita. Ruokalista perustuu parhaisiin raaka-aineisiin, saaristolaisuuteen ja kotimaisiin ruokasesonkeihin. Rapupaikkana Ravintola Särkänlinna on aina ollut ehdoton. 
