INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3709, 'Ravintola Söder', 'Ravintola Söder tarjoaa mahtavia alkuruokia ja jälkiruokia josta voi koota upean kokonaisuuden oman maun mukaan. Piipahda vaikka lasilliselle viiniä ja pienelle makupalalle tai porukalla nauttimaan koko menusta!', 'http://www.restaurantsoder.fi', 'https://tprimages.blob.core.windows.net/public/3709/e1eddd5f-28d8-4eeb-b8e1-96a825a7fa8c.jpg', 'Uudenmaankatu 9', '00120', 'Helsinki', '2022-01-30 17:05:37.107826+02', 10);
