INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3930, 'Ravintola Tammisto', 'Ravintola Tammisto Vantaalla on moderni ravintolakokonaisuus. Päivisin lounasravintolana toimiva Ravintola Tammisto tarjoaa myös upeat puitteet juhliin tai kokouksiin kaikenkokoisille ryhmille.', 'https://www.ravintolatammisto.fi/', 'https://tprimages.blob.core.windows.net/public/3930/9b34daed-ea34-4b1a-bd16-c0ed6e3104b7.jpg', 'Sähkötie 8', '01510', 'Vantaa', '2022-01-30 17:05:37.111548+02', 55);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2266, 'Ravintola Toca', 'Ravintola Toca tekee maittavaa italialaista ruokaa suomalaisella vivahteella, ja tarjoilee mukavia juomia rennolla otteella. Tocassa tarjoillaan kaikkea mielenkiintoista alkuillan aperitiiveistä myöhäisiin erikoisherkkuihin. 
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3397, 'Ravintola Tokyo55', 'Ravintola Tokyo55, joka perustettiin 2008, on yksi ensimmäisiä sushiravintoloita Helsingissä. Ravintolan ruokalista perustuu aitoon japanilaiseen keittiöön, jota on sovellettu eurooppalaiseen makuun, sekä laadukkaaseen sushiin.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (362, 'Ravintola Torni', 'Ravintola Tornissa tarjoillaan hotelliaamiaisen lisäksi lounasta, illallista sekä lauantaisin brunssia. Ravintola Torni on kuuluisa taidokkaasta keittiöstään sekä historiallisesta ja taiteellisesta tunnelmastaan. Ravintola Torni on toiminut vuodesta 1931 alkaen sijaiten Hotelli Tornin alakerrassa. Tornin keittiössä käytetään laadukkaita raaka-aineita ja arvostetaan käsityötä, annokset kootaan kauniisti lautaselle ja lopputulos on laadukas, maistuva ja kaunis. 
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2159, 'Ravintola Troikka', 'Troikka on venäläinen ravintola Töölön ytimessä. Troikan tunnelmassa on juuri sitä lämpöä, joka kuuluu venäläiseen vieraanvaraisuuteen. Tarjolla Venäläisen keittiön täyteläinen makumaailma ja sen parhaimmat herkut.', 'https://troikka.fi/', NULL, 'Caloniuksenkatu 3', '00100', 'Helsinki', '2022-01-30 17:05:37.118338+02', 28);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2929, 'Ravintola Tuk Tuk', 'Pieni thaimaalainen ravintola Vaasankadulla jonka annoksissa mausteita ei ole säästelty. Suositun ravintolan annoksia saa myös mukaan mikäli ei mahdu tai halua jäädä ravintolaan ruokailemaan.', 'https://www.facebook.com/ravintolatuktukhelsinki/', NULL, 'Vaasankatu 19', '00500', 'Helsinki', '2022-01-30 17:05:37.11983+02', 2);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3318, 'Ravintola Ukko-Munkki', 'Ravintola Ukko-Munkissa on toiminut vuodesta 1954 saakka Munkkiniemessä. Yläkerran ravintolasalissa voi nauttia ravintolan ruoista ja alakerran Ugari-pubissa mukavasta meiningistä. Ravintola noudattaa 50-luvulta tuttua perinteikästä henkeä ja tyyliä.', 'http://www.ukko-munkki.fi/', NULL, 'Laajalahdentie 19', '00330', 'Helsinki', '2022-01-30 17:05:37.121243+02', 27);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (80, 'Ravintola Uunisaari', 'Uunisaarella sijaitseva Ravintola Uunisaari on viihtyisä ravintola jossa a-oikeudet, kahvila aurinkoisin terassein sekä kaksi saunaa. Uunisaaren ravintolan päärakennus saneerattiin v. 1999 museoviraston valvonnassa. Vanhat punatiiliseinät luovat ravintolaan lämpimän ja juhlallisen tunnelman. 
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (1013, 'Ravintola Variantti', 'Ravintola Variantti toimii arkipäivisin kahvila- ja lounasravintolana. Iltaisin ja viikonloppuisin Variantti muuntuu asiakastilaisuuksien tapahtuma- ja juhlapaikaksi.', 'https://www.taitotalo.com/fi/ravintolat/variantti/', NULL, 'Valimotie 8', '00380', 'Helsinki', '2022-01-30 17:05:37.124552+02', 54);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (206, 'Ravintola Walhalla', 'Ravintola Walhalla on Suomenlinnassa, Kustaanmiekan saaren linnakkeessa, sijaitseva tasokas gourmetravintola vuodelta 1952. Ravintolan pääsalina on kaunis kaponieeri Delwig. Ravintolan yhteydessä toimii myös pizzeria Nikolai, näköalaterassi ja baari. 
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (4121, 'Hampton Bay', 'Hampton Bay on viihtyisä rantaravintola, joka tarjoaa mahdollisuuden nauttia merellisestä Helsingistä hyvien ruokien ja juomien kera. Lisäksi tarjolla on ketobrunssi lauantaisin.', 'https://www.hamptonbay.fi/', NULL, 'Hietalahdenranta 6', '00120', 'Helsinki', '2022-01-30 17:05:37.365721+02', 39);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (1104, 'Ravintola Wellamo', 'Ravintola Wellamo on viihtyisä ruokaravintola Katajanokalla joka on tarjonnut hengen ja ruumiin kulttuuria jo vuodesta 1975. Wellamossa hyvä ruoka syntyy rakkaudella parhaista raaka-aineista, gastronomisia elämyksiä luodaan mutkattomassa miljöössä. Monet listalla olevista ruoista ovat klassikkoja jo vuosien takaa. 
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (4090, 'Ravintola Willihanhi', 'Ravintola Willihanhi on rento kortteliravintola Malminkartanossa jonka ruokalistalta löytyy niin suomalaisia kuin intialaisiakin makuja sekä pizzaa.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (1206, 'Ravintola WTC Helsinki', 'Ravintola WTC tarjoaa maukkaan ja monipuolisen lounaan jokaisena arkipäivänä hurmaavissa, vanhoissa arkkitehti Pauli Blomstedtin suunnittelemissa pankkitalon tiloissa. 
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (363, 'Ravintola Zetor', 'Zetor tarjoaa reilua ruokaa ja jäljittelemätöntä meininkiä Kaivopihalla, Helsingin ydinkeskustassa. Traktorien kupeessa kelpaa haukata hiukopalaa tai nostella kursailematta kuppia. Zetorin ruokalista ja juomalista henkivät suomalaisuutta: tuttuja klassikoita ja maaseudun inspiroimia erikoisuuksia.', 'https://www.raflaamo.fi/fi/helsinki/zetor', NULL, 'Mannerheimintie 3-5, Kaivopiha', '00100', 'Helsinki', '2022-01-30 17:05:37.136826+02', 30);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3899, 'Ravintola Zilla', 'Zilla on Vantaan Tikkurilassa sijaitseva, iloa niin arkeen kuin juhlaan hyvän juoman, ruoan kuin fiiliksenkin avulla tuova ravintola. Zilla on ainutlaatuinen kombo rentoa meininkiä, mojovia cocktaileja ja laadukkaita viinejä sekä suussa sulavia ruokia.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2566, 'Restaurant Caverna', 'Caverna on viihtyisä tuhannen neliön luolaravintola, jossa voi kokea maailmankeittiön makuja aidoista brasilialaisista lihavartaista aina Japanista tuttuun teppanyaki-kokkaukseen. Lounas- ja illallisbuffet.', 'https://www.caverna.fi/', NULL, 'Yliopistonkatu 5', '00100', 'Helsinki', '2022-01-30 17:05:37.140384+02', 30);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3450, 'Restaurant Natura', 'Natura on alan nuorten ja intohimoisten ammattilaisten perustama ravintola, jossa tinkimätön laatu ja rento illanvietto kulkevat käsi kädessä. Henkilökunnan ammattitaito pohjautuu kokemukseen yhden, kahden ja kolmen tähden Michelin-ravintoloissa Suomessa, Ranskassa, Englannissa, Espanjassa ja Italiassa. 
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (4150, 'Rioni', 'Rioni, Espoon Haukilahdessa, on perinteisistä georgialaisista resepteistä ammentava ravintola. Rehelliset ja ryhdikkäät annokset toimivat  lämminhenkisessä ja tunnelmallisessa ravintolassa kaiken pohjana. Rioniin on helppo tulla yksin, kaksin tai koko seurueen kanssa.', 'https://www.rioni.fi/', NULL, 'Ukkohauentie 9', '02170', 'Espoo', '2022-01-30 17:05:37.143623+02', 58);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2809, 'Ristorante Vaelsa', 'Vaelsan keittiö yhdistää lähiruokaa ja klassisia italialaisia herkkuja modernilla otteella.', 'https://vaelsa.fi/', 'https://tprimages.blob.core.windows.net/public/2809/b7114802-393f-4447-81e8-d9d5bdd78cb2.jpg', 'Pohjoisesplanadi 9', '00100', 'Helsinki', '2022-01-30 17:05:37.146564+02', 20);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3534, 'Royal Nepal', 'Royal Nepal on nepalilainen ravintola Vallilassa.', 'https://royalnepal.fi', NULL, 'Sturenkatu 32-34', '00550', 'Helsinki', '2022-01-30 17:05:37.1481+02', 32);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3987, 'Saigon House Cafe', 'Saigon House Cafe on pieni kahvila jossa tarjolla kahvin lisäksi tarjolla aitoa Vietnamilaista aamiaista sekä lounasannoksia.', 'https://fi-fi.facebook.com/SaigonHouseCafe', NULL, 'Eerikinkatu 9', '00100', 'Helsinki', '2022-01-30 17:05:37.149602+02', 10);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (4211, 'Sakata Cuisine', 'Sakata Cuisine on japanilainen ravintola Postitalossa, Rautatieaseman vieressä. Ravintola tarjoilee mm. gyoza dumplingeja, ramenkeittoa, sobanuudeleita, sushia jne.', 'https://www.sakatacuisine.com', NULL, 'Elielinaukio 2 G, Postitalo 2krs.', '00100', 'Helsinki', '2022-01-30 17:05:37.151012+02', 30);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (1133, 'Salve', 'Ravintola Salve on kiinteä osa Kampin ja erityisesti Hietalahden historiaa. Ravintolan kunniakas taival alkoi pienestä kioskista, jonka luukusta myytiin ruokaa ja tupakkaa lähinnä merimiehille jo vuonna 1897.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (4138, 'SameSame', 'SameSame - But Different Punavuoren sydämessä on ravintola autenttisella aasialaisella Asian mix-keittiöllä ja leppoisalla SameSame - but different ambience -fiiliksellä.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3483, 'SAMRAT Indian Cuisine', 'Ravintola Samrat tarjoaa monipuolisia herkkuja eri puolilta Intiaa yli kahden kymmenen vuoden kokemuksella. Valikoimaan kuuluu mm. tandoori-ruoat, tikat sekä masalat.', 'http://www.samrat.fi/', NULL, 'Eteläesplanadi 22 C', '00130', 'Helsinki', '2022-01-30 17:05:37.155562+02', 49);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2725, 'Sandro Kortteli', 'Suosittu ja tunnelmallinen Sandro tarjoaa pohjoisafrikkalaisia, välimerellisiä ja Lähi-idän makuja päivän joka hetkeen lounaasta ilta myöhään. Viikonloppuisin tarjolla jo klassikoksi muodostunut Marrakech Madness Brunch.', 'https://www.sandro.fi/', NULL, 'Urho Kekkosen katu 1, Kampin keskus 5.krs', '00100', 'Helsinki', '2022-01-30 17:05:37.156965+02', 10);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (4076, 'Sandwich Club', 'Sandwich Club on delityylinen sandwich ja bagel ravintola Torikortteleissa, Senaatintorin laidalla.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2017, 'Santa Fe', 'Santa Fé on eksoottinen tuulahdus Meksikosta keskellä Helsinkiä. Mausteisen texmex-ruoan, nachojen, tortillojen ja burritojen lisäksi ruokalistalla on myös cajun-keittiön herkkuja ja barbeque-annoksia. Kesällä katettu terassi tarjoaa kolmen ravintolan ruokia.', 'http://www.santafe.fi/', NULL, 'Aleksanterinkatu 15, sisäpiha', '00100', 'Helsinki', '2022-01-30 17:05:37.159875+02', 30);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (1468, 'Satkar', 'Nepalilainen ravintola Satkar avattiin vuonna 1998 ja on avaamisensa jälkeen saavuttanut laajan asiakaspiirin sekä tasokkaan ruoan että ystävällisen palvelun ansiosta. Satkar on Helsingin suurin nepalilainen ravintola.', 'https://www.satkar.fi/', NULL, 'Fredrikinkatu 46', '00100', 'Helsinki', '2022-01-30 17:05:37.161438+02', 10);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (549, 'Sea Horse', 'Suomalaista ravintolakulttuuria henkivä ravintola Sea Horse, tunnettu myös nimellä Sikala, on toiminut jo vuodesta 1934.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3603, 'Sen Chay', 'Vegaanista aasialaistyyppistä ruokaa tarjoava ravintola Hakaniemessä', 'https://fi-fi.facebook.com/Sen-Chay-169008529957447/', NULL, 'Siltasaarenkatu 3-5', '00530', 'Helsinki', '2022-01-30 17:05:37.164323+02', 36);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (1376, 'Seurasaaren Kruunu', 'Seurasaaressa sijaitseva ravintola Seurasaaren Kruunu tarjoaa monipuolisia kahvilatuotteita, lohikeittolounasta, laadukkaan á la carte listan kesäkaudella sekä kylmiä ja kuumia juomia. Terassilla voi nauttia upeasta merinäköalasta Seurasaarenselälle. 
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2607, 'Shelter', 'Shelter tuo yhteen hyvän ruoan ja juoman ystävät Katajanokan rannassa sijaitsevan makasiinirakennuksen suojissa. Tarjolla on a la carten lisäksi myös Chef''s Menu, 3, 4 tai 5 ruokalajin yllätysmenut.', 'http://www.shelter.fi', NULL, 'Kanavaranta 7', '00170', 'Helsinki', '2022-01-30 17:05:37.167203+02', 52);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3756, 'Sikke''s', 'Sikke''s on kodikas korttelikuppila, kuin ystävän olohuone. Sen ystävän, joka valmistaa aina hyvää ruokaa. Saatat saada lihapullia napolilaisittain, rapeaksi paistettua, vastapyydystettyä kokonaista kalaa tai vaikka jänispataa ranskalaisittain tai Italian tyyliin. Ravintolan baariin voi myös pistäytyä viinilasilliselle. Viinin kanssa tarjolla on kaikenlaista pientä hyvää, baarissa voi myös nauttia täyden aterian.', 'https://sikkes.fi/', 'https://tprimages.blob.core.windows.net/public/3756/d58aa3f3-32bf-484b-b6b4-0a0ec5f331d7.jpg', 'Tehtaankatu 34 D', '00150', 'Helsinki', '2022-01-30 17:05:37.168713+02', 48);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (1593, 'Siltanen', 'Siltanen on takuuvarma valinta, sillä viikon lähes jokaisena iltana järjestetään erilaisia klubi-iltoja. Ilta kymmeneen saakka tila toimii ravintola Ondana, mutta auringon laskettua saavat pöydät siirtyä tanssilattian alta. Viikonloppuisin itse pöydillä saatetaan jopa tanssia. Siltasen sisällä sijaitseva ravintola Onda tarjoilee mm. suurilta osin vegaanista- ja  kasvisruokaa, sekä lauantaisin perulaista brunssia.', 'https://siltanen.org/', NULL, 'Hämeentie 13 B', '00530', 'Helsinki', '2022-01-30 17:05:37.170098+02', 25);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (1596, 'Silvoplee', 'Silvoplee on Satu Silvon vuonna 1999 perustama monipuolinen raaka-ruokaan erikoistunut kasvisravintola Hakaniemessä. Seisovasta pöydästä voi valinnan mukaan koostaa aterian joko elävän ravinnon raikkaista antimista tai maukkaista lämpimistä kasvisruoista. Tarjolla päivän lämmintä ruokaa, salaatteja, bowleja, gluteenitonta leipää, vege-tapaksia, raakaleivoksia ja muita herkkuja, kahvilatuotteita, supermehuja ja smootheja ja paljon muuta!', 'https://silvoplee.fi/', NULL, 'Toinen linja 3', '00530', 'Helsinki', '2022-01-30 17:05:37.171606+02', 36);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3522, 'Singapore Hot Wok', 'Kampin kauppakeskuksen Quick and Tasty-alueella E-tasolla sijaitseva Singapore Hot Wok tarjoaa nopeaan ruokailuun soveltuvaa kiinalaista ja malesialaista ruokaa.', 'https://kamppihelsinki.fi/fi/yritys/singapore-hot-wok', NULL, 'Urho Kekkosen katu 1, E-taso, Kampin kauppakeskus', '00100', 'Helsinki', '2022-01-30 17:05:37.173389+02', 10);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3497, 'Taverna Zorbas', 'Taverna Zorbas on korkeatasoinen kreikkalainen ravintola Alppilassa Brahen kenttää vastapäätä. Ravintola tarjoilee maukkaita kreikkalaisia kasvis-, kala- ja liharuokia sekä herkullisia jälkiruokia.', 'http://zorbas.fi/', NULL, 'Läntinen Brahenkatu 4', '00510', 'Helsinki', '2022-01-30 17:05:37.174686+02', 2);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (71, 'Sipuli', 'Vanhaan punatiiliseen satamamakasiiniin rakennettu Sipuli on poikkeuksellisen monipuolinen ravintolakokonaisuus joka toimii pääsääntöisesti tilausravintolana. Ravintola avaa myös ovensa esim. joulun aikaan tarjoten joululounasta noutopöydästä.', 'https://www.ravintolasipuli.fi/', NULL, 'Kanavaranta 7', '00160', 'Helsinki', '2022-01-30 17:05:37.176002+02', 52);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2425, 'Skiffer Erottaja', 'Skiffer on rustiikki, moderni kohtauspaikka Helsingin sydämessä, Diana-puiston reunalla Erottajankadulla. Ravintolan sydän on kiviarinauuni, jossa paistetut liuskat lyövät lautaselta perinteiset pizzat. Rapeapohjaisten liuskojen lisäksi Erottajalla tarjotaan myös herkullisia salaatteja. Skifferin viinilista on tehty sydämellä ja harkitusti.', 'https://www.skiffer.fi/', NULL, 'Erottajankatu 11', '00130', 'Helsinki', '2022-01-30 17:05:37.177323+02', 49);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (4316, 'Skutta Ravintola', 'Skutta on taide- ja kulttuuribistro, jonka lautasella luonto ottaa vallan. Arkisin lounaalla maistuu sesonki ja villiluonto. Lauantaisin Skutassa tarjoillaan Wienerschnitzeleistä lohtubrunsseihin. Skutassa järjestetään myös kulttuuritapahtumia ja taiteesta vastaa Galleria Rankka.', 'https://skuttaan.fi/', NULL, 'Turunlinnantie 1, Kulttuurikeskus Stoa', '00900', 'Helsinki', '2022-01-30 17:05:37.178597+02', 26);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3092, 'Skiffer Kallio', 'Skiffer Kallio on rustiikki, moderni kohtauspaikka Kallion sydämessä. Ravintolan sydän on kiviarinauuni, jossa paistetut liuskat lyövät lautaselta perinteiset pizzat. Rapeapohjaisten liuskojen lisäksi tarjolla on myös herkullisia salaatteja.', 'https://www.skiffer.fi/', 'https://tprimages.blob.core.windows.net/public/3092/d5aa3e39-1c27-461c-af5c-ffccbf193cff.jpg', 'Fleminginkatu 8', '00530', 'Helsinki', '2022-01-30 17:05:37.179827+02', 36);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2192, 'Skiffer Liuskaluoto', 'Helsingissä Kaivopuiston edustalla sijaitsevalla Liuskaluodolla pääset nauttimaan merellisestä ilmastosta Skifferin tunnelmallisella terassilla. Liuskoja eli gourmetpizzoja on tarjolla heti kevään tultua sään salliessa aina elokuun loppuun asti päivittäin, ja syyskuussa säävarauksella.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (4013, 'Skiffer Töölö', 'Töölön Skifferissä pääsee nauttimaan Skifferin perinteisestä tunnelmasta ja kuuluista herkuista, kuten Liuskoista ja salaateista.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3707, 'Skiffer Viiskulma', 'Skiffer Viiskulma on Skiffer-ketjun lippulaivaravintola jossa on mukava nauttia lasi viiniä ja pientä huikopalaa tai isompaan nälkään Skifferin herkullisia Liuskoja.  
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3351, 'Social Burgerjoint Citycenter', 'Master Chef 2012 voittajan Mika Tuomosen burgeripaikka. Listalta löytyy erilaisia puuhiiligrillissä valmistettuja burgereita, myös kasvis- ja vegaanivaihtoehtoja, lisukkeita unohtamatta. Social Burgerjointin konsepti on simppeli: pihvi on pääroolissa jokaisessa burgerissa.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3385, 'Sofia Helsinki', 'Senaatintorin ja Kauppatorin välissä sijaitseva Sofia Helsinki on mitä parhain paikka tehdä töitä, kohdata ihmisiä nenäkkäin ja nauttia ravintolan herkuista.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3454, 'Sofia Ravintola ja Cafe & Bar', 'Sofia Ravintola ja Cafe & Bar sijaitsee Sofia Helsinki co-workingtiloissa Torikortteleissa.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (1939, 'Soppakeittiö', 'Soppakeittiö on tarjonnut keittoja vuodesta 2003. Alusta asti itse tehdyt keitot, päivittäin tarjolla kolmea erilaista keittoa. Sopan seuraksi tarjotaan myös tuoretta leipää.', 'https://www.facebook.com/SoppakeittioVanhakauppahalli/', NULL, 'Eteläranta, Vanha Kauppahalli', '00100', 'Helsinki', '2022-01-30 17:05:37.190683+02', 49);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2807, 'Southpark Restaurant', 'Southpark on Sinebrychoffin puiston laidalla sijaitseva kaunis ja tunnelmallinen ravintola, joka tarjoilee arkisin maukasta lounasta ja viikonloppuisin runsaan ja herkullisen brunssin. Vieressä toimii Sinebrychoffin taidemuseo.', 'https://www.southparkrestaurant.fi/', NULL, 'Bulevardi 40, Sinebrychoffin puisto', '00120', 'Helsinki', '2022-01-30 17:05:37.192035+02', 39);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3442, 'Starter Restaurant', 'Aamiaisen tapaamispaikka, lounaan kohtaamispaikka ja iltapäivän palaveripaikka. Osa startup keskittymä Maria 01:stä, joka on rakennettu vanhan Marian sairaalan tiloihin.', 'https://starterhelsinki.com/', 'https://tprimages.blob.core.windows.net/public/3442/ad400e4f-b518-42dd-9ba6-900c861d1f1b.jpg', 'Lapinlahdenkatu 16, rakennus 15 C', '00180', 'Helsinki', '2022-01-30 17:05:37.193465+02', 10);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2254, 'Stefan''s Steakhouse', 'Stefan''s Steakhousessa paistuvat pohjoisen parhaimpiin lukeutuvat pihvit. Muun muassa Amerikan Top Chef -ohjelmasta tutun huippukokin, Stefan Richterin, mukaan nimetty pihviravintola tarjoilee annoksia, joiden näyttävyys ja maistuvuus mitataan suuren maailman asteikolla. 
