INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3892, 'Story Tripla', 'Bistro Café Storyn yhtälö on lyömätön: kaunis ja eläväinen miljöö, huippuluokan keittiömestarit ja kondiittorit, viinien sekä palvelun asiantuntijat sekä rakkaus käsityöhön ja raaka-aineisiin. Story on helposti lähestyttävä palveluun, ystävällisyyteen ja laatuun satsaava kokonaisuus joka muotoutuu tarpeiden mukaan kahvilasta huippuravintolaksi ja kaikkea siltä väliltä.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2934, 'Story Vanha Kauppahalli', 'Story on kahvila-ravintola Vanhassa Kauppahallin keskiössä, jossa tarjotaan mutkatonta halliruokaa, pientilaviinejä, kahvila- ja konditoriatuotteita sekä aamiaista ja brunssia.', 'https://www.story-restaurants.fi/', 'https://tprimages.blob.core.windows.net/public/2934/e7a4d4ff-7ce3-4e2c-ba1d-3e59d4b24c21.jpg', 'Eteläranta', '00130', 'Helsinki', '2022-01-30 17:05:37.199127+02', 49);
