INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2014, 'Tapas BarCelona', 'Tapas BarCelona on tunnelmallinen tapasbaari kaupungin ytimessä Aleksin Wanhan Kauppakujan suojassa. Arkipäivisin tarjolla on lounasta seisovasta pöydästä, viikonloppuisin ja iltaisin nautitaan tapaksia viinin, oluen ja muiden juomien kera. Tapaksien salaisuutena ovat perinteiset ja korkealaatuiset raaka-aineet suomalaisia raaka-aineita unohtamatta.', 'https://www.raflaamo.fi/fi/helsinki/tapas-barcelona', NULL, 'Aleksanterinkatu 46', '00100', 'Helsinki', '2022-01-30 17:05:37.224817+02', 30);
