INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2802, 'Tenho Restobar', 'Tenho Restobar on hyvään ruokaan ja loistaviin iltoihin intohimoisesti suhtautuva ravintola Helsinginkadulla. Tunnettu maukkaista pizzoistaan ja ilmaisista livekeikoista. Ruokalistalta löytyy myös vegaanisia vaihtoehtoja, salaatteja, hampurilaisia sekä liha- ja kalaruokia. Aina vapaa sisäänpääsy.', 'http://www.ravintolatenho.fi/', NULL, 'Helsinginkatu 15', '00500', 'Helsinki', '2022-01-30 17:05:37.228176+02', 2);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (621, 'Teatteriravintola Nyyrikki', 'Ravintola Nyyrikki sijaitsee lähellä Helsingin keskustaa, matkalla kohti lentoasemaa, Puukäpylän kainalossa. Valoisa ja tunnelmaltaan kodikas perheyritys on valmiina palvelemaan. Keittiössä valmistuvat mehukkaat pihvit ja hienoimmat suomalaiset kalat rakkaudella ja ammattitaidolla.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2900, 'Teurastamon portti - Roslundin lihamyymälä ja bistro', 'Teurastamon Portti on Roslundin lihamyymälä ja bistro Teurastamon alueella. Myymälä sijaitsee alueen sisäänkäynnin pittoreskissa portinvartijan rakennuksessa. Perinteisen lihakaupan valikoiman lisäksi tarjolla on herkullista kotiruokalounasta sekä hiiligrillissä valmistettuja lihaisia herkkuja, kuten pihvejä ja hampurilaisia. Kesäaikaan myös terassi.', 'https://www.roslund.fi/teurastamon-portti/', NULL, 'Työpajankatu 2', '00580', 'Helsinki', '2022-01-30 17:05:37.231302+02', 43);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3171, 'The Boom Room', 'The Boom Roomin värikkäässä tarjonnassa yhdistyvät thaimaalaisen ja japanilaisen keittiön maukkaimmat ainesosat. Pikkuravintolan annoksissa maistuu persoonallisuus, tuoreet raaka-aineet sekä sydämellinen tekemisen into. Lounastarjontaan kuuluvat erityisesti sushi- ja teriyakiannokset sekä sashimisalaatti. Paikan päällä huomion kiinnittävät myös erinomainen pienkahvilan tunnelma ja laajalti ylistetyt raakakakut.', 'https://www.facebook.com/theboomroomhelsinki/', NULL, 'Iso Roobertinkatu 46', '00120', 'Helsinki', '2022-01-30 17:05:37.232569+02', 39);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2588, 'The Cock', 'Kasarmitorin kulman The Cock on lounaasta myöhään iltaan auki oleva eläväinen kortteliravintola ja baari, jonne kaikki ovat tervetulleita. 
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3402, 'The Dockyard', 'Välitön kortteliravintola Hernesaaressa.', 'http://www.thedockyard.fi/', NULL, 'Hernesaarenkatu 17', '00150', 'Helsinki', '2022-01-30 17:05:37.235121+02', 18);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (4148, 'The One Thai Kitchen', 'The One Thai Kitchen on autenttisten thaimaalaisten elämysten koti Vantaan Tikkurilassa. Ravintola tarjoaa raikkaan tuulahduksen Thaimaan lämpöä, ennenkokemattomia makuja sekä autenttisen thai-keittiön klassikoita ja erikoisuuksia aina tuoreista raaka-aineista valmistettuna. 
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3111, 'The Old Skiffer', 'The Old Skiffer on merihenkinen ja rento baari ja ravintola jossa voit nauttia raikkaista juomista ja Skifferin liuskoista.', 'https://www.skiffer.fi/ravintolat/the-old-skiffer.html', NULL, 'Laivurinkatu 10', '00150', 'Helsinki', '2022-01-30 17:05:37.237696+02', 39);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3981, 'The Saigonese', 'The Saigonese on pieni tunnelmallinen ravintola keskustassa, lähellä Kampin kauppakeskusta. Ravintola tarjoilee vietnamilaista kotiruokaa jonka alkuperä on Saigonissa, etelä-Vietnamissa. Ruokalistalla on niin liha- kuin vegaanivaihtoehtoja. Lisäksi vietnamilaista kahvia, jälkiruokia sekä mocktaileja.', 'https://the-saigonese.com/', 'https://tprimages.blob.core.windows.net/public/3981/2083399a-acac-4f01-b320-6719ef3f9e26.jpg', 'Lapinlahdenkatu 21', '00180', 'Helsinki', '2022-01-30 17:05:37.239096+02', 10);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3161, 'The TRAY', 'Hotelli Glo Kluuvin ravintola The Trayn ruokalistan perustan muodostavat runsaat ja luovasti kootut vatiannokset. Cross kitchenin ja street foodin parhaat puolet innovatiivisella tavalla yhdistävä ravintola kuljettaa kävijänsä vastustamattomalle makumatkalle maailman toreille ja kujille.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (4123, 'Tikke Kebab Grill', 'Tikke Kebab Grill on ravintola Kontulan ostoskeskuksessa joka tarjoilee aitoa shish kebabia niin jauhetusta- kuin kokolihastakin, ja myös sisäelimistä valmistettuna.', 'https://www.facebook.com/tikkekebabgrill', NULL, 'Kontulankaari 1', '00940', 'Helsinki', '2022-01-30 17:05:37.241717+02', 5);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3922, 'Tokumaru Tripla', 'Tokumaru haluaa tuoda japanilaisen keittiön rikkaat maut mahdollisimman autenttisessa muodossa ruuan ystäville. Tokumaru tuo itse maahan suurimman osan hyvän ruuan valmistamiseen tarvittavista aineksista suoraan Japanista.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3908, 'Tokyo Street Helsinki', 'Ravintola Tokyo Street on aito japanilainen ravintola Hietalahden hallissa. 
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3947, 'ToQyo Food Street -ravintola', 'Tokiota sanotaan maailman ruokapääkaupungiksi ja katuruokakulttuurilla on siellä pitkät perinteet. Klassikkoannokset, kuten nuudelipainotteinen Ramen, eväslaatikko Bento, lihapata Gyudon sekä höyrysämpylä Bao muodostavat ToQyo Food Streetin menun. Uniikit annokset ja maut ovat helposti lähestyttäviä ja nopean ja ravitsevan ruoan voi nauttia joko paikan päällä tai ottaa sen mukaan.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (1469, 'Toscanini', 'Toscanini tarjoaa aitoa italialaista ruokaa rennossa ympäristössä. Keittiössä käytetään erittäin korkealaatuisia ja aitoja raaka-aineita, joista valmistetaan herkullisia, mutkattomia ja runsaita annoksia vieraiden nautittavaksi.', 'https://www.toscanini.fi/', 'https://tprimages.blob.core.windows.net/public/1469/e81edecb-ab5d-4f71-b40e-00a28d479297.jpg', 'Bulevardi 2-4', '00120', 'Helsinki', '2022-01-30 17:05:37.247016+02', 10);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2227, 'Torikorttelit', 'Torikorttelit on uudella elämällä täyttynyt Helsingin historiallinen keskusta. Kantakaupungin vanhimpien rakennusten ja niiden lomassa risteilevien sisäpihojen luoma erityinen tunnelma on houkutellut alueelle kaikkea kiinnostavaa: ravintoloita sekä kahviloita modernista perinteiseen ja pikaisesta pitkään kaavaan, kivijalkaputiikkeja, salakapakan, oman radioaseman, kaupunginmuseon ja lasten museon, elokuvateatterin ja tapahtumasaleja. Kesällä kadut ja sisäpihat ovat täynnä terasseja, ja kaupungintalon sisäpihalta löytyy ainutlaatuinen keidas, maailman pienin keskuspuisto.', 'https://www.torikorttelit.fi/', NULL, 'Unioninkatu-Sofiankatu-Katariinankatu', '00170', 'Helsinki', '2022-01-30 17:05:37.248431+02', 20);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (4212, 'Trattoria il Faro', 'Ravintola il Faron ruokalistalta löytyy laajasti Välimeren ruokia, lännestä aina itäisimpään kolkkaan asti. 
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3015, 'Trattoria Rivoletto', 'Rivoletto on tarjonnut kaupungin parhaimpia pizzoja sekä muita italialaisherkkuja jo vuodesta 1971. Ruoka valmistetaan suurella sydämellä itse ja tuoreista raaka-aineista kuten aitoon italialaiseen keittiöön kuuluu. Rustiikkisessa ja tunnelmallisessa ravintolassa nauttii upean makuelämyksen hyvien viinien ja kahvien kera.', 'https://rivoletto.fi', NULL, 'Albertinkatu 38', '00180', 'Helsinki', '2022-01-30 17:05:37.251087+02', 10);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3854, 'Trattoria Sogno', 'Sognossa niin ystävät, perheet sekä työkaverit kohtaavat mutkattoman ja maukkaan italialaisen ruuan parissa. Sogno on uudenlainen, rento ja raikas tuulahdus Töölössä. 
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2859, 'Tukkutorin kala', 'Tukkutorin Kala on laajan ja tuoreen valikoiman asiantunteva kala- ja äyriäiskauppa sekä lounasravintola. Palvelutiskit ovat täynnä herkullisia tuoreita kaloja ja muita mereneläviä. 
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2855, 'Turkkilainen ravintola Efesos', 'Turkkilainen ravintola Efesos tarjoilee aitoja turkkilaisia makuja ravintolassa joka ennen sijaitsi Myllypuron Teboil-kylmäaseman yhteydessä, mutta joka on muuttanut Vantaalle ja sijaitsee nyt Sokevan talossa Korsossa.', 'http://turkkilainenravintola.fi/', NULL, 'Tavitie 1', '01450', 'Vantaa', '2022-01-30 17:05:37.256905+02', 7);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3393, 'Twisted Street Kitchen', 'Twisted Street Kitchen on Aasia-vaikutteinen lounasravintola Helsingin keskustassa joka on tunnettu raikkaasta aasialaisen katukeittiön mausta ja rennosta meiningistä. Lounasaikaan listalta löytyy jo klassikoiksi muodostuneet lämpimän mausteiset kookos-curryt, liharuuat ja salaatit. Kaikissa annoksissa maistuu ravintolan oma twisti.', 'https://www.twistedhelsinki.fi/', NULL, 'Fredrikinkatu 58', '00100', 'Helsinki', '2022-01-30 17:05:37.259352+02', 28);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3343, 'Töölön Sävel', 'Bistro Töölön Sävel sijaitsee kivenheiton päässä Töölöntorista. Sali on avara, ilmava ja valoisa; sisustuksen 50-lukulaisuus samoin kuin menneiden vuosikymmenten iskelmämusiikki luovat rennon tunnelman. Listalla on niin perinteisiä annoksia kuin esim. flammkucheneita ja burgereita. Sunnuntaisin tarjolla on brunssi.', 'https://toolonsavel.fi/', NULL, 'Runeberginkatu 40', '00260', 'Helsinki', '2022-01-30 17:05:37.261832+02', 16);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (460, 'Töölönranta', 'Töölönranta on tyylikäs kohtauspaikka Töölönlahden rannassa aivan Suomen Kansallisoopperan vieressä. Valoisa ravintolasali ja viihtyisä takkabaari tarjoavat vuodenaikojen mukaan muuttuvat avarat näkymät Hesperian puistoon ja Töölönlahdelle. Töölönrannan keittiö on modernin skandinaavinen ottaen vaikutteita Euroopasta.', 'https://www.toolonranta.fi/', NULL, 'Helsinginkatu 56', '00260', 'Helsinki', '2022-01-30 17:05:37.264323+02', 16);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2689, 'Umeshu', 'Umeshu on intohimoisesti japanilaiselle ruoalle ja juomalle omistautunut intiimi ravintola Töölössä. Jo vuodesta 2008 Umeshussa sushit kuin lämpimätkin annokset valmistettu käsityönä huippulaatuisista raaka-aineista avokeittiössä suoraan asiakkaiden silmien edessä. Sesonkien mukaan vaihtuvalta ruokalistalta löytyy perinteisiä japanilaisia annoksia, sekä tietenkin Umeshun tyyliin hiottuja kokonaisuuksia.', 'https://www.umeshu.fi/', NULL, 'Pohjoinen Hesperiankatu 15', '00260', 'Helsinki', '2022-01-30 17:05:37.266807+02', 16);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (596, 'UniCafe Kaivopiha', 'UniCafe on opiskelijaruokala ja se toimii Helsingin yliopiston kaikilla kampuksilla.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (4125, 'Uzbekistan', 'Ravintola Uzbekistan on lämminhenkinen ravintola Kontulassa. Uzbekistanin tunnelma syntyy kahdesta päätekijästä, jotka ovat reilu ruoka ja rento palvelu. Ravintola Uzbekistaniin mahtuvat isommatkin seurueet herkuttelemaan, sekä lounas- että illallisaikaan.', 'https://www.ravintolauzbekistan.fi/', NULL, 'Ostostie 4', '00940', 'Helsinki', '2022-01-30 17:05:37.27241+02', 5);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2025, 'Vapiano', 'Italialainen pikaruokaravintola jossa pastat, pizzat, alkuruoat ja salaatit tilataan tiskin yli suoraan kokeilta, jotka valmistavat ruoan asiakkaan silmien edessä. Vapianon pitkät tammipöydät kutsuvat keskustelemaan yhdessä ystävien, perheen tai muiden ravintolavieraiden kanssa. 
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (4025, 'Vegoshi', 'Vegoshi tarjoilee helpon tavan syödä terveellistä, herkullista ruokaa.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3511, 'Vibami', 'Vibami on vietnamilainen ravintola joka tarjoilee vietnamilaista katuruokaa etelä-vietnamilaiseen tyyliin.', 'https://www.vibami.fi/', NULL, 'Eerikinkatu 14, 2.krs', '00100', 'Helsinki', '2022-01-30 17:05:37.28065+02', 10);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3724, 'Vöner Lippakioski', 'Vegaaniruokaa vuodesta 2016 keskellä Helsinkiä - nyt yhteistyössä Vönerin kanssa! Tarjolla on vanhoja ja uusia grilliherkkuja vöneröitynä, ja kaikki tietysti vegaanisena.', 'https://www.facebook.com/vonerlippakioski', NULL, 'Museokatu 12', '00100', 'Helsinki', '2022-01-30 17:05:37.283169+02', 28);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (10038, 'Davai Davai ', 'Davai Davaissa neuvostovaltiot palaavat takaisin laseihin ja lautasille.', 'https://davaidavai.fi/', 'https://tprimages.blob.core.windows.net/public/10038/036ad68e-8aaa-42a1-8ded-d78d02c290a9.jpg', 'Pohjoisesplanadi 17', '00170', 'Helsinki', '2022-01-30 17:05:37.547856+02', 20);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2801, 'Via Tribunali', 'Pizzeria Via Tribunali tarjoaa perinteistä napolilaista pizzaa, jota voidaan pitää yhtenä maailman parhaista katuruoista. Ravintolan lista on lyhyt ja hyvin perinteinen, pizzat paistuvat yli 450-asteisessa avotuliuunissa ja valmistuvat noin minuutissa!', 'https://viatribunali.fi/', NULL, 'Sofiankatu 4', '00170', 'Helsinki', '2022-01-30 17:05:37.285652+02', 20);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (4005, 'Via Tribunali Kallio', 'Vuonna 2017 ensimmäisen autenttisen napolilaisen pizzerian Helsingin Sofiankadulle avannut Pizzeria Via Tribunali laajeni Helsingin Kallioon loppukesästä 2019.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (392, 'Viikin kartano', 'Kustavilainen Viikin kartano on kaunis kokous- ja juhlapaikka Viikin yliopistoalueen kupeessa Helsingissä. Viihtyisät, entisöidyt tilat huokuvat idyllistä kartanoromantiikkaa ja rauhallisessa puutarhassa ajatukset lentävät. Kartanosalin ikkunoista aukeaa maisema ruusutarhoille, viljellyille pelloille ja lehmälaitumille sekä niiden takana sijaitsevalle Vanhankaupungin luonnonsuojelualueelle. Pihan puolella on vanhoja tammia ja lehmuksia. Arkisin kartanolla tarjoillaan lounasta klo 10.30-13.30 välillä.', 'https://www.sodexo.fi/ravintolat/helsinki/viikin-kartano', NULL, 'Viikinkaari 8', '00790', 'Helsinki', '2022-01-30 17:05:37.290675+02', 37);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2201, 'Viikinkiravintola Harald', 'Viikinkiravintola Harald on mystisestä viikinkiajasta mausteensa saava elämysruokaravintola.', 'https://www.ravintolaharald.fi/', NULL, 'Aleksanterinkatu 21, Citykäytävä 2krs.', '00100', 'Helsinki', '2022-01-30 17:05:37.293592+02', 30);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2975, 'Villetta - Ristorante e Pizzeria', 'Italialainen pizzeria-trattoria Ruusulankadulla Helsingin Töölössä.', 'http://www.villetta.fi/', NULL, 'Ruusulankatu 8', '00260', 'Helsinki', '2022-01-30 17:05:37.29634+02', 16);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2605, 'Vinkkeli', 'Ravintola Vinkkeli sijaitsee Kaartinkaupungissa. Historiallisessa kiinteistössä toimiva ruoka- ja viiniravintola tarjoaa nuorten yrittäjien tuoreen näkövinkkelin ravintolaelämykseen, jonka ytimessä ovat välitön tunnelma, tinkimättömästi valmistetut klassikkoannokset ja pieteetillä valitut viinit.', 'https://www.ravintolavinkkeli.fi/', NULL, 'Pieni Roobertinkatu 8', '00130', 'Helsinki', '2022-01-30 17:05:37.298827+02', 49);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3332, 'Väinö Kallio', 'Väinö Kallio on tilausravintola ja tapahtumatila jossa järjestetään myös yleisölle avoimia tapahtumia. Väinö Kalliossa koet mm.konsertteja, teatteria, tanssia, iloja ja draamoja. Rakennus on Väinö Vähäkallion suunnittelema, vuonna 1928 rakennettu entinen Elannon pääkonttori.', 'https://www.vainokallio.fi/', NULL, 'Hämeentie 11', '00530', 'Helsinki', '2022-01-30 17:05:37.301573+02', 25);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (4079, 'W30', 'Ravintola W30 on makumatka ranskalais-skandinaaviseen keittiöön. Ravintolan menut rakennetaan kauden tuoreista raaka-aineista ja ruoan valmistuksessa käytetään erilaisia tekniikoita kulinaarisen kokemuksen maksimoimiseksi.', 'https://www.restaurantw30.fi/', 'https://tprimages.blob.core.windows.net/public/4079/81eb3a4d-5511-4458-857f-ec9ae3436882.jpg', 'Unioninkatu 30', '00100', 'Helsinki', '2022-01-30 17:05:37.305084+02', 30);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (4171, 'WaffDaddy', 'Rapeaa, maukasta ja gluteenitonta belgialaistyylistä vohvelia, kasviksilla, kaloilla, lihalla. Makeita annoksia unohtamatta.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2864, 'Wino', 'Trendikäs viinibaari ja ravintola Kallion sydämessä. Wino on erikoistunut luomu- ja alkuviineihin, joita osaa saa myös laseittain. Kauniit ruoka-annokset ja rento ilmapiiri luovat onnistuneen illan viinien parissa.', 'https://www.wino.fi/', NULL, 'Fleminginkatu 11', '00500', 'Helsinki', '2022-01-30 17:05:37.310242+02', 36);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3516, 'Xiao Mei Lin Dumplings', 'Kiinalainen ravintola Xiao Mei Lin, "pieni Mei Lin", on Annankadulla sijaitsevan kiinalaisravintola Mei Linin "pikkusisko". Tarjolla on mm. dumplingeja, wonton-keittoja, kiinalaisia salaatteja sekä japanilaista sakea.', 'https://www.xiao-meilin.com/', NULL, 'Lönnrotinkatu 33-35', '00180', 'Helsinki', '2022-01-30 17:05:37.312769+02', 10);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3081, 'Yes Yes Yes', 'Yes Yes Yes on uusi eloisa helsinkiläisbaari ja -ravintola kukoistavalla Iso Roobertinkadulla. Ravintolan erikoisuus on tulikuuma tandooriuuni sekä pitkät baaritiskit, jotka toimivat yhtä hyvin raikkaiden drinkkien siemailuun kuin myös ruokailuun. Odota värien ilotulitusta, banaanipuita ja rutkasti marmoria yhdistettynä ajankohtaisimpaan paikalliseen taiteeseen. Kesäaikaan Yes Yes Yesin terassilta, tässä klassisessa kulmauksessa, on mukava seurailla kaupungin elämää ja tilata vaikkapa lasillisen kylmää roseeta.', 'https://www.yesyesyes.fi', NULL, 'Iso Roobertinkatu 1', '00120', 'Helsinki', '2022-01-30 17:05:37.315341+02', 39);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3569, 'Zinnkeller', 'Ravintola Zinnkeller tarjoaa aitoa saksalaista perinneruokaa ja iloista tunnelmaa Kruununhaassa. Ravintola Zinnkeller on perheyritys joka on toiminut vuodesta 1980 lähtien. Monipuoliselta listalta löytyy Saksan eri alueiden herkut, viinejä ja oluita unohtamatta.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (1594, 'Zucchini', 'Alusta alkaen itsetehtyä laktovegetaarista kasvisruokaa ja kahvilatuotteita jo vuodesta 1993. Myös vegaanisena.', 'https://www.facebook.com/Kasvisravintola-Zucchini-2033302090217984/', NULL, 'Fabianinkatu 4', '00130', 'Helsinki', '2022-01-30 17:05:37.323458+02', 49);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3917, 'Ludvig', 'Korkeavuorenkadun ja Ludviginkadun kulmassa sijaitseva Ludvig on public house-tyylinen ravintola joka tarjoaa asiakkailleen herkullista pubiruokaa, laadukkaan olutvalikoiman sekä mielenkiintoisia viinejä - tasokkaita tisleitä unohtamatta. Kesällä sisäpihan kaunis terassi kutsuu viihtymään lämpimänä kesäiltana.', 'https://publudvig.fi/', NULL, 'Ludviginkatu 1', '00130', 'Helsinki', '2022-01-30 17:05:37.32584+02', 49);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (4157, 'Albina Ravintola & Viinibaari', 'Ravintola Albina sijaitsee Vallilan Konepajan alueella, The Folks Hotel Konepajan kivijalassa.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2800, 'Alfons'' Pizza', 'Suosittu artesaanipizzeria Ullanlinnassa. Kuuluisa pizzojensa rapeista luomupohjista ja rennosta tunnelmasta.', 'http://www.alfonspizza.fi/', NULL, 'Vuorimiehenkatu 35', '00150', 'Helsinki', '2022-01-30 17:05:37.330931+02', 29);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2182, 'Hard Rock Cafe Helsinki', 'Helsingin Hard Rock Café on 240-paikkainen ruoka- ja juomaravintola, jossa on kansainvälinen ja leppoisa tunnelma. Keittiö tarjoilee klassista jenkkiläistä ruokaa höystettynä suomalaisilla mauilla. Katutasossa sijaitsee The Rock Shop®, josta löydät ainutlaatuiset Hard Rock Cafe Helsinki -tuotteet', 'https://www.hardrockcafe.com/location/helsinki/', NULL, 'Aleksanterinkatu 21', '00100', 'Helsinki', '2022-01-30 17:05:37.368348+02', 30);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3572, 'Allas Restaurants', 'Allas Restaurants, Allas Sea Pool merikylpylän yhteydessä, kokoaa yhteen kolme erilaista ravintolakokemusta. Allas Café & Terrace, Allas Bistro & Lounge ja Allas Sky Bar.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (4191, 'Bar 10', 'Bar 10 on helposti lähestyttävä ja viihtyisä korttelikapakka Kalliossa. Mutkaton ravintolan ja baarin yhdistelmä, jossa hinnat ovat maltilliset ja annokset kookkaita. Uudenmaankadun legendaarisen Ysibaarin sisarravintola.', 'https://bar10.fi/', NULL, 'Fleminginkatu 10', '00530', 'Helsinki', '2022-01-30 17:05:37.335815+02', 36);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2797, 'Bar Loosister', 'Rockväen ja muidenkin kansanosien olohuone Rockbistro Bar Loosister aukesi toukokuussa 2013 Helsingin Kurviin. Ravintolan keskeinen ajatus on täyttää alkuperäisen Bar Loosen jättämä tyhjiö pääkaupungin ravintolakartalla. Bar Loosisterista löydät leppoisaa tunnelmaa, vaihtuvaa musiikkia, pienpanimo-oluita ja konstailematonta sapuskaa, myös vegaania.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2314, 'Kuja Bar & Bistro', 'Persoonallinen bistrohenkinen kortteliravintola eläväisen Hakaniemen torin kupeessa. Legendaariset burgerit. Ravintolaan on myös koirat tervetulleita. 
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2764, 'Bastion Bistro', 'Bastion Bistrossa pääsee nauttimaan mutkatonta ja maistuvaa bistroruokaa kaikkia aisteja hellivässä, kiireettömässä miljöössä, pienen kävelymatkan päässä Suomenlinnan päälaiturista. Ruokalistan raaka-aineet pyritään saamaan vastuullisesti lähellä tuotettuina ja valmistettuina, unohtamatta Suomenlinnan oman panimon juomia. Valmistamisessa korostuvat perinteiset saaristolaiset valmistusmenetelmät, kuten grillaaminen, liekittäminen, hauduttaminen ja keittäminen.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (1174, 'Belge Bar & Bistro', 'Belge bar&bistro on mutkaton belgialainen olut- ja seurusteluravintola kahdessa kerroksessa. Belgialaiset rakastavat hyvää ruokaa ja juomaa niin laadullisesti kuin määrällisesti. Tämä on myös Belgen ohjenuora; tarjota asiakkailleen elämän hyviä puolia. Olut kaadetaan oikein ja annokset ovat reiluja ja rehellisiä. 
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (584, 'Bollis bar & bistro', 'Bolt Arenan katutasossa sijaitseva Bollis Bar & Bistro tarjoaa lounasta arkisin sekä peli-iltoina näkymän kentälle. Ravintola aukeaa tuolloin tuntia ennen ottelun alkua ja sulkeutuu pelin päätyttyä.', 'https://www.bollis.fi/fi/etusivu', NULL, 'Urheilukatu 5', '00250', 'Helsinki', '2022-01-30 17:05:37.345773+02', 16);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3951, 'Broo Pizza & Bar', 'Uusi Broo Pizza & Bar sijaitsee Helsingin ytimessä, kivenheiton päässä Kampin ostoskeskuksesta.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (1132, 'Bruuveri', 'Panimoravintola Bruuveri on tunnelmallinen oluen nautiskelijoiden ja muiden herkkusuiden kohtauspaikka Helsingin sydämessä, Kampin ostoskeskuksessa. 
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (1434, 'Café Bar No 9', 'Cafe Bar No 9 on toiminut olohuoneena kultturelleille kaupunkilaisille jo vuodesta 1996. Ihmiset, eli henkilökunta ja asiakkaat, ovat se tärkein asia ravintolan suosion takana. Ravintolan seinillä on kuukausittain vaihtuvaa kotimaista taidetta. Keittiön klassikoiksi ovat muodostuneet mm. kanakorianteri -wok ja Pollo Limonello -pasta sekä koko päivän tarjottava aamiaislautanen.', 'http://bar9.net/', NULL, 'Uudenmaankatu 9', '00120', 'Helsinki', '2022-01-30 17:05:37.353051+02', 10);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (1999, 'Café Talo', 'Cafe Talo on sopiva sekoitus street baaria, ruokailua, teatteriravintolaa sekä yksityistilaisuuksia. Kahden kerroksen asiakastilat tarjoaa nuorekkaan ja trendikkään vaihtoehdon keskustan kuppiloille.', 'http://www.cafetalo.fi/', NULL, 'Hämeentie 2 B', '00530', 'Helsinki', '2022-01-30 17:05:37.35554+02', 36);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2672, 'Casa Largo', 'Casa Largo on moderni ja välitön espanjalainen ravintola Helsingin sydämessä, Sokoksen kulmassa. Täällä tapaksien nauttiminen on rentoa seurustelua ilman turhaa pönötystä ja keittiön loihtimat herkulliset maut paellasta crema catalanaan sekä monipuolinen viinilista saavat viihtymään vaikka koko illan.', 'https://www.raflaamo.fi/fi/helsinki/casa-largo', NULL, 'Asema-aukio 2', '00100', 'Helsinki', '2022-01-30 17:05:37.357983+02', 30);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (4091, 'Flavourium', 'Flavourium inspiroituu eri lokaatioista, niiden mauista ja kulinaristisista kulttuureista. Jokainen annos on oma kokonaisuutensa, joka vie makumatkalle eri kohteisiin Flavouriumin tyyliin.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2729, 'Gastro Pub Kustaa Vaasa', 'Gastro Pub Kustaa Vaasa Vaasankadulla on toiminut samalla nimellä samassa osoitteessa vuodesta 1962, pitkään pelkkänä baarina mutta nykyään tarjolla on myös ruokaa.', 'https://www.kustaavaasa.fi/', NULL, 'Vaasankatu 10', '00500', 'Helsinki', '2022-01-30 17:05:37.363006+02', 2);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3916, 'Helsinki Beer Factory', 'Helsinki Beer Factory on rento olut- ja ruokaravintola jossa hyvää ruokaa, mm. hampurilaisia ja pitkään grillissä kypsytettyjä lihoja, sekä monipuolinen valikoima käsityöläisoluita ja viskejä.', 'https://www.facebook.com/pages/category/Brewery/Helsinki-Beer-Factory-1810876675905318/', NULL, 'Humikkalanrinne 1', '00940', 'Helsinki', '2022-01-30 17:05:37.371289+02', 5);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2603, 'Holiday Bar', 'Holidayn sijainti on täydellisesti ydinkeskustan tuntumassa, Helsingin suosituimpien turistikohteiden ympäröimänä, Kanavarannan vanhassa makasiinirakennuksessa. Äyriäisiä ja kalaa, modernia kasvisruokaa, freesejä drinkkejä, jäätelöä ja iso, aurinkoinen terassi. Rento ja tunnelmallinen kohtaamispaikka syömiseen, juomiseen ja viihtymiseen ystävien kanssa.', 'https://holiday-bar.fi/', NULL, 'Kanavaranta 7', '00170', 'Helsinki', '2022-01-30 17:05:37.374085+02', 52);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (4243, 'Junk y Vegan', 'Junk y Vegan on uusi Sanomataloon avattu "total vegan joint", ravintola ja baari jossa tarjotaan maistuvaa, modernia, terveellistä, rentoa ja täysin vegaanista junk food -safkaa. Ravintolan terassi A-oikeuksin houkuttelee nostamaan maljan kuohuvaa tai vaikkapa Kombuchaa!', 'http://junkyvegan.fi/', NULL, 'Postikuja 2', '00100', 'Helsinki', '2022-01-30 17:05:37.377133+02', 30);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2195, 'Korjaamon Baari & Keittiö', 'Korjaamon Baari & Keittiö on mutkaton kortteliravintola kulttuurin keskellä. Ravintola kutsuu rennolle lounaalle, illalliselle, brunssille tai ihan vain lasilliselle. Kesällä on mahdollista ruokailla myös ravintolan aurinkoisella terassilla.', 'https://www.korjaamo.fi/fi/ruoka-ja-juoma', NULL, 'Töölönkatu 51 a-b', '00250', 'Helsinki', '2022-01-30 17:05:37.380005+02', 16);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3624, 'Kultá Kitchen & Bar', 'Kultá Kitchen & Bar tarjoilee aidoimmat ja puhtaimmat Lapin maut modernilla tavalla valmistettuina. Pohjoisen rento, vieraanvarainen ja kiireetön elämäntyyli näkyvät ensiluokkaisessa palvelussa, ja ravintolan sisustus henkii pohjoisen harmonista tunnelmaa. 
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3233, 'La Bottega 13', 'Your closest experience to Italy''s finest!
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2761, 'Linko Pizzabar', 'Linko PizzaBar on laadukas korttelikuppila Töölössä. Ruoan ja juoman lisäksi Linkossa pääsee usein myös nauttimaan kulttuurin antimista, esimerkiksi hyvästä livemusiikista.', 'http://www.linkopizzabar.fi/', NULL, 'Ruusulankatu 3-5', '00260', 'Helsinki', '2022-01-30 17:05:37.390036+02', 16);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2904, 'Malaga Bar', 'Malaga Bar on Stiina Kuisman ravintola ja baari Jätkäsaaressa, muusikoiden talo Jallukan alakerrassa, Malagakadun ja Välimerenkadun kulmassa. Monet muistavat Stiinan legendaariset ravintolat Ysibaarin ja Torin. Sitä samaa henkeä: hyvää ruokaa ja ilonpitoa tuodaan nyt loistavan tiimin kanssa Jätkäsaareen.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (540, 'Manala', 'Legendaarinen Manalan ruokaravintola ja baari tarjoaa herkullista ruokaa ja juomaa sekä mutkatonta palvelua aina lounaasta valomerkkiin asti. Manalan Kellarissa biletetään joka viikonloppu, ja tarjolla on myös erilaisia klubeja esim. live-musiikin tai improteatterin ystäville. St.Urho''s pub on konstailematon englantilaistyyppinen olutravintola jonka listalla on yli sata olutmerkkiä.', 'https://www.manala.fi/fi/', NULL, 'Dagmarinkatu 2', '00100', 'Helsinki', '2022-01-30 17:05:37.394964+02', 28);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3036, 'Maxill', 'Vuonna 1992 perustettu Maxill tunnetaan ravintolana, jonka leppoisa ilmapiiri on sekä keittiön että uskollisen asiakas­kunnan ansiota. A la carte-lista perustuu kansainväliseen keittiöön, jossa eniten vaikuttaa Ranska, Italia ja Skandinavia. 
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (4227, 'Niska Helsinki', 'Niska Helsinki tarjoaa yksinkertaisen, mutta sitäkin makurikkaamman pizza-menun ahvenanmaalaiseen tyyliin. Peltileiviksi kutsutut pizzat paistetaan korkealuokkaisista, pääosin Ahvenanmaalla tuotetuista raaka-aineista ja tarjoillaan merellistä tunnelmaa henkivissä tiloissa.', 'https://www.niskahelsinki.com/', NULL, 'Kanavaranta 3-7 D 19', '00160', 'Helsinki', '2022-01-30 17:05:37.399917+02', 52);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2908, 'Pizzorante BRO', 'Pizzorante BRO on silta herkulliseen italialaiseen ruokaan. Ravintolan  keihäänkärkenä on ohut ja rapeapohjainen roomalaistyyppinen pizza. Keittiö valmistaa ruuat Italialaisella intohimolla ja suomalaisella sydämellä. Laadukas makuelämys toteutuu yhdistämällä Italian alueelliset eroavaisuudet sekä laadukkaat raaka-aineet kotimaasta ja Italiasta helsinkiläisellä twistillä. Pizzorante BRO on osa Hilton Helsinki Strandia.', 'https://www.pizzorantebro.fi', NULL, 'John Stenbergin ranta 4', '00530', 'Helsinki', '2022-01-30 17:05:37.402325+02', 36);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (480, 'Ravintola 10. Kerros', 'Vuonna 1947 ensimmäisen kerran avattu, ja syyskuussa 2020 uudistettu Ravintola 10. Kerros tarjoaa maiseman, jonka jokainen haluaa nähdä.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (832, 'Ravintola Juttutupa', 'Juttutupa eli Juttis on auki joka päivä ja sinne ovat tervetulleita kaikki. Juttis on aito, ainutlaatuinen ja raikkaan kansanomainen, sinne mahtuvat sulassa sovussa niin tavalliset helsinkiläiset kaduntallaajat, taiteen alan työläiset kuin elämän kiiltävään puoleen mieltyneet kaupunkilaisetkin. Mutkattomuus, ihmisläheisyys, elävä musiikki ja konstailematon ruoka ovat niitä Juttutuvan omimpia juttuja. Siellä viihdytään hyvässä seurassa ja rehdin ruoan parissa - useimmiten pitkään.', 'https://www.juttutupa.fi/', NULL, 'Säästöpankinranta 6', '00530', 'Helsinki', '2022-01-30 17:05:37.407558+02', 36);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (10047, 'SLO Bros Hakaniemi', 'SLO Bros Hakaniemi tarjoaa tacoja, kalifornialaisella twistillä.', 'https://slobros.fi', 'https://tprimages.blob.core.windows.net/public/10047/3c481e26-32da-450d-89f9-079be854b233.jpg', 'Porthaninkatu 1', '00530', 'Helsinki', '2022-01-30 17:05:37.55038+02', 36);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3203, 'Ravintola Mullikka', 'Mullikka on kotoisa kortteliravintola Mannerheimintien varrella, entisissä Primulan kahvilan tiloissa, joka panostaa erityisesti runsaaseen kotimaiseen pienpanimovalikoimaan. Ravintolasta löytyy 8 vaihtuvaa oluthanaa ja useita kymmeniä pullovaihtoehtoja. Laajan olutvalikoiman lisäksi ravintolassa voi herkutella loistavilla burgereilla ja muilla herkuilla.', 'https://www.mullikka.fi/', NULL, 'Mannerheimintie 130', '00270', 'Helsinki', '2022-01-30 17:05:37.409978+02', 53);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2819, 'Ravintola Musta Härkä', 'Ravintola Musta Härkä tarjoaa viihtyisät tilat ja tunnelmallisen ympäristön ruokailua ja hauskaa illanviettoa varten Helsingin Vallilassa. Tilassa toimii intialainen ravintola Rasoi joka tarjoilee lounasta (arkisin) sekä a la carte-ruokalistan antimia myöhäiseen iltaan (ma-la). Mustan Härän ohjelmistossa on viikoittain DJ ja karaoke, sekä mukava kattaus live-artisteja, urheilua unohtamatta.', 'http://www.mustaharka.fi', NULL, 'Mäkelänkatu 52', '00510', 'Helsinki', '2022-01-30 17:05:37.412344+02', 32);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (490, 'Ravintola Teatteri', 'Tässä Helsingin sykkivässä sydämessä Esplanadin puiston alkupäässä viihdytään aamukahvista yömyssyyn. Mielenkiintoinen ja monipuolinen ravintolakokonaisuus kätkee sisäänsä Teatterin Grillin, Teatteribaarin, Kellobaarin, Klubin ja Delin.', 'https://www.teatteri.fi/', NULL, 'Pohjoisesplanadi 2', '00130', 'Helsinki', '2022-01-30 17:05:37.414605+02', 49);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3928, 'Wanda''s Kitchen & Lounge', 'Wanda''s Kitchen & Lounge on vantaalainen, rennon tyylikäs ravintolakokonaisuus, joka tarjoaa lounaan, à la carte -annosten ja brunssin lisäksi Singa-karaokea viikonloppuisin sekä live-musiikkia lauantai-iltaisin. Lisäksi catering-palvelu sekä sauna- ja kokoustiloja. Lounasta tarjolla arkisin 10.30-14.', 'https://www.wandas.fi/', 'https://tprimages.blob.core.windows.net/public/3928/fc7b3593-934d-4cd5-b85d-0c22cb29c383.jpg', 'Liesikuja 5', '01600', 'Vantaa', '2022-01-30 17:05:37.458798+02', 23);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3262, 'Ravintola Viisi Penniä', 'Olut- ja ruokaravintola Viisi Penniä on perinteinen helsinkiläinen krouvi joka aukesi Töölön tullin vieressä v. 1956. Yhdessäoloon houkutteleva alakerta, viihtyisä toinen kerros sekä aurinkoinen terassi tarjoavat luontevan miljöön seurusteluun, oluiden nautiskeluun sekä maukkaaseen ruokailuun.  Ravintolassa on 20 oluthanan rivistö sekä monipuoliset viski- ja viinivalikoimat. Pullo-oluita ja siidereitä on tarjolla lähes 160 erilaista.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3530, 'Ravintola Plein', 'Plein on viihtyisä korttelibistro Puu-Vallilan kupeessa. Pieniä, laadukkaita jaettavia annoksia ja naturalviinejä.', 'https://www.plein.fi/', 'https://tprimages.blob.core.windows.net/public/3530/5aecfcdb-ddc6-4bd7-8b2f-46e1bfd36a31.jpg', 'Suvannontie 18', '00510', 'Helsinki', '2022-01-30 17:05:37.419764+02', 32);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3398, 'Rosa Izakaya', 'Rosa Izakayassa voi maistaa uniikkeja cocktaileja, rentoutua viinilasin ääreessä tai nauttia kylmästä oluesta. 
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3806, 'Salpimienta', 'Salpimienta on Kalliossa sijaitseva baari ja ravintola, jonka vahvuuksia ovat monipuolinen olutvalikoima, pizzat, kebab ja salaatit.', 'http://www.salpimienta.fi', NULL, 'Fleminginkatu 7', '00530', 'Helsinki', '2022-01-30 17:05:37.42477+02', 36);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3653, 'Satama Bar & Bistro', 'Kalasatamassa sijaitsevassa lounasravintolassa on arkisin klo 10.30-14.30 tarjolla runsas, pääosin vegaaninen, värikäs buffetlounas. Lounaan ulkopuolella on tarjolla bistromenu jossa mm. erilaisia salaatteja, tahnoja jne.', 'https://www.satamabar.com/', NULL, 'Työpajankatu 13 A', '00580', 'Helsinki', '2022-01-30 17:05:37.427351+02', 25);
