INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (1979, 'Stone''s', 'Stone''s tuo elämästä nautiskelevien ulottuville parhaista lähitoimittajien raaka-aineista valmistettuja burgereita sekä kattavan valikoiman erikoisoluita vaativampaankin makuun. Ruoassa ja juomassa painotetaan paikallisuutta ja luomua, sillä asiakkaille halutaan tarjota aitoja makuelämyksiä.', 'https://www.raflaamo.fi/fi/helsinki/stones', NULL, 'Keskuskatu 4', '00100', 'Helsinki', '2022-01-30 17:05:37.432515+02', 30);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3324, 'The Bull & The Firm', 'Baari jonka erikoisuuksiin kuuluu cocktailit, viinit (erityisesti ekologiset- ja alkuviinit) sekä herkulliset maisteluannokset.', 'http://thebullandthefirm.fi/', NULL, 'Kulmavuorenkatu 2', '00500', 'Helsinki', '2022-01-30 17:05:37.435294+02', 25);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (4154, 'The Glass', 'The Glass on rento bistroravintola ja rommibaari Valokuvataiteen museon K1-näyttelytilan yhteydessä, Kämp Gallerian alimmassa kerroksessa. 
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3007, 'Tislaamo - Distillery Bar', 'The Helsinki Distilling Companyn cocktail baari ja ravintola Tislaamo - Distillery Bar tarjoilee talon omista tuotteista valmistettuja drinkkejä, sekä Yhdysvaltojen syvän etelän inspiroimaa ruokaa. 
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2851, 'Treffi Pub & Bistro', 'Treffi Pub & Bistro on hiiligrillillä varustettu kortteliravintola Herttoniemen metroasemaa vastapäätä. Lähiöklassikko Treffi Pub & Bistro on monien tuntema kohtaamispaikka, jossa sunnuntaiaamut on pyhitetty brunsseille jonka teemat vaihtuvat viikoittain.', 'https://treffipub.com/', 'https://tprimages.blob.core.windows.net/public/2851/4a4ace6d-26e4-40d0-9812-01718d62a443.jpg', 'Hiihtomäentie 14', '00810', 'Helsinki', '2022-01-30 17:05:37.442862+02', 3);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3960, 'Urban Rabbit', 'Urban Rabbit on sopivasti rosoinen ja sielukas kohtaamispaikka Triplassa. Tiskin takana intohimoiset ammattilaiset loihtivat maailmanluokan cocktailit ja vinkkaavat parhaat erikoisoluet, joiden kanssa maistuu urbaani bistroruoka.', 'https://www.raflaamo.fi/fi/helsinki/urban-rabbit', NULL, 'Fredikanterassi 1, Mall of Tripla', '00520', 'Helsinki', '2022-01-30 17:05:37.445482+02', 11);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3434, 'Ventuno, Osteria - Caffé', 'Maailman metropolien moderneista italialaisravintoloista inspiroitunut Ventuno kattaa Italian parhaat maut tuorepastoista cocktaileihin.', 'https://www.ventuno.fi/', 'https://tprimages.blob.core.windows.net/public/3434/49cf2724-0f3a-496c-bf00-bdd2c3419492.jpg', 'Korkeavuorenkatu 21', '00130', 'Helsinki', '2022-01-30 17:05:37.448156+02', 29);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (4017, 'Version', 'Version ihastuttaa Kalifornian keittiön runsaan makumaailman inspiroimilla annoksilla sekä vehreällä puutarhamaisella ympäristöllä. Versionin Drinkery tarjoilee huipputason cocktaileja. Poikkea aamiaiselle, lounaalle, illalliselle tai cocktaileille. 
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (4069, 'Viinibaari Apotek', 'Historiallinen Helsingin XII Joutsen apteekki toimi yli sadan vuoden ajan Lapinlahdenkadun ja Eerikinkadun kulmassa.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (951, 'Vltava', 'Vltava on tekkiläinen ravintola Elielinaukiolla Rautatieaseman kupeessa. Yli 100-vuotiaan jugendrakennuksen kolme erilaista ravintolakerrosta tarjoavat tunnelmallisen ympäristön tutustumisille, tapaamisille, seurustelulle ja hauskanpidolle maailman parhaiden oluiden parissa, tekkiläisten ruokien siivittämänä.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (3207, 'Woolshed Australian Gastro Pub', 'Rento australialaisvetoinen ravintola Rautatieaseman lähellä. Tarjolla oluita, juomia sekä mutkatonta ruokaa huokeaan hintaan, kuten esim. burgereita, hummeria ja australialaisia pihvejä. Viikonloppuisin mm. coverbändit soittavat livemusiikkia eikä urheiluakaan ei ole unohdettu, ota vain vähän juotavaa tai  purtavaa ja nauti toiminnasta isolta screeniltä.', 'https://www.woolshed.eu/', NULL, 'Töölönlahdenkatu 3 B 1', '00100', 'Helsinki', '2022-01-30 17:05:37.461371+02', 30);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (4329, 'Ageno', 'Ravintola Ageno on uusi nepalilainen ravintola Vallilassa. Henkilökunnan ammattitaito takaa unohtumattoman makuelämyksen. Ravintolassa on tarjolla monenlaisia herkkuja kasvis-, kana-, lammas- ja kalaruokia sekä vegaanille sopivia ruokia.', 'http://ageno.fi', NULL, 'Sturenkatu 31', '00550', 'Helsinki', '2022-01-30 17:05:37.464055+02', 32);
