INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2203, 'Hietalahden kauppahalli', 'Yli 100-vuotias Hietalahden kauppahalli palvelee viihtyisänä kahvila- ja ravintolahallina. Kansainväliset keittiöt tarjoavat herkullisia makuelämyksiä ympäri maailman. Hallista löytyy myös monipuolisia lahjoja kaikkiin tilaisuuksiin.', 'https://www.hietalahdenkauppahalli.fi/', NULL, 'Lönnrotinkatu 34', '00180', 'Helsinki', '2022-01-30 17:05:37.498989+02', 10);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (503, 'Kosmos', 'Hepolampien Kosmos on ollut osa Helsingin kulinaarista kulttuuria vuodesta 1924. Alkuperäisessa kunnossaoleva ravintolasali on jo yksin nähtävyys. Keittiö edustaa helsinkiläistä ruokaperinnettä vorschmackista tartariin ja kateenkorvasta silakoihin.', 'https://kosmos.fi/', NULL, 'Kalevankatu 3', '00100', 'Helsinki', '2022-01-30 17:05:37.501721+02', 10);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (89, 'Ravintola China', 'Vuodesta 1973 toiminut Ravintola China on Helsingin ensimmäinen kiinalainen ravintola. 65-paikkainen, Etelä-Kiinan kantonilaiseen keittiöön erikoistunut korkealaatuinen ravintola sijaitsee keskustassa Annankadulla.
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (471, 'Ravintola Elite', 'Elite on ainutlaatuinen kulttuuri- ja taiteilijaravintola, elämän estradi ja taiteilijoiden olohuone vuodesta 1932. Tässä oikean elämän teatterissa ovat viihtyneet niin näyttelijät kuin muusikot, kirjailijat ja tanssijat, ja myös kuvataiteilijat, joiden teokset nyt koristavat Eliten seiniä. Suomalais-kansainvälisen keittiön antimia on mm. Tauno Palon kermasipulipihvi sekä vorschmack.', 'https://www.elite.fi/', NULL, 'Eteläinen Hesperiankatu 22', '00100', 'Helsinki', '2022-01-30 17:05:37.509933+02', 28);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (505, 'Ravintola KuuKuu', 'Ravintola KuuKuu on hyvän ruoan kotimainen kortteliravintola. Ruokatyyli on sopiva sekoitus perinteisiä makuja ja modernia keittotaitoa. Viihtyisässä baarissa voi laatujuomien kanssa nauttia ravintolan keittiössä valmistettuja pikkuannoksia. Kesällä palvelee myös aurinkoinen terassi johon voi pysähtyä ruokailemaan tai vaikka vain lasilliselle.', 'https://kuukuu.fi/', NULL, 'Museokatu 17', '00100', 'Helsinki', '2022-01-30 17:05:37.512496+02', 28);
INSERT INTO scoop.venue (id, name, description, info_url, img_url, street_address, postal_code, city, created_at, neighbourhood_id) VALUES (2895, 'Ravintola Ora', 'Sasu Laukkosen luotsaama pieni 23-paikkainen ravintola Ora avautui elokuussa 2017 entisen Chef & Sommelierin tiloissa Huvilakadulla. Ravintolan keittiö paneutuu suomalaiseen sesonki- ja lähiruokaan modernilla fine dining otteella. Illallisella on tarjolla useamman ruokalajin ruokasesongeittain vaihtuva menu. 
